Property.Value:
BEGIN SET NOCOUNT ON;
DECLARE @JnTableSchema nvarchar(255), @JnTableName nvarchar(255), @FkTableSchema nvarchar(255), @FkTableName nvarchar(255);
-- Add ubiquitous views for all join tables. DECLARE EACHTABLE_CURSOR CURSOR READ_ONLY FOR SELECT [TableSchema], [TableName], [FkTableSchema], [FkTableName] FROM [adm].[vDbColumnInfo] WHERE (@DictatedTableSchema IS NULL OR @DictatedTableSchema = [TableSchema]) AND LEFT([TableName], 2) = 'jn' AND [ColumnId] = 3 ORDER BY [TableSchema], [TableName] OPEN EACHTABLE_CURSOR FETCH NEXT FROM EACHTABLE_CURSOR INTO @JnTableSchema, @JnTableName, @FkTableSchema, @FkTableName WHILE (@@fetch_status <> -1) BEGIN
IF (@IsDebug = 1) BEGIN PRINT (CONCAT(@JnTableSchema, '; ', @JnTableName, '; ', @FkTableSchema, '; ', @FkTableName)); EXECUTE [adm].[usp_CreateUbiquitousVeiw] @JnTableSchema, @JnTableName, @FkTableSchema, @FkTableName, 1; PRINT ''; END IF (@IsDebug = 0) EXECUTE [adm].[usp_CreateUbiquitousVeiw] @JnTableSchema, @JnTableName, @FkTableSchema, @FkTableName, 0;
FETCH NEXT FROM EACHTABLE_CURSOR INTO @JnTableSchema, @JnTableName, @FkTableSchema, @FkTableName END CLOSE EACHTABLE_CURSOR; DEALLOCATE EACHTABLE_CURSOR
-- Add ubiquitous views for all standard tables. DECLARE EACHTABLE_CURSOR CURSOR READ_ONLY FOR SELECT DISTINCT [TableSchema], [TableName] FROM [adm].[vDbColumnInfo] WHERE (@DictatedTableSchema IS NULL OR @DictatedTableSchema = [TableSchema]) AND LEFT([TableName], 2) != 'jn' AND LEFT([TableName], 2) != 'lk' ORDER BY [TableSchema], [TableName] OPEN EACHTABLE_CURSOR FETCH NEXT FROM EACHTABLE_CURSOR INTO @JnTableSchema, @JnTableName WHILE (@@fetch_status <> -1) BEGIN
IF (@IsDebug = 1) BEGIN PRINT (CONCAT(@JnTableSchema, '; ', @JnTableName)); EXECUTE [adm].[usp_CreateUbiquitousVeiw] NULL, NULL, @JnTableSchema, @JnTableName, 1; PRINT ''; END IF (@IsDebug = 0) EXECUTE [adm].[usp_CreateUbiquitousVeiw] NULL, NULL, @JnTableSchema, @JnTableName, 0;
FETCH NEXT FROM EACHTABLE_CURSOR INTO @JnTableSchema, @JnTableName END CLOSE EACHTABLE_CURSOR; DEALLOCATE EACHTABLE_CURSOR
END

-- =============================================
-- Author:		Markus Schippel
-- Create date: 2024-04-04
-- Description:	Used to create/replace all the standard (ubiquitous) table views. 
-- =============================================
-- [adm].[usp_CreateAllUbiquitousVeiws] 'dbo';
-- [adm].[usp_CreateAllUbiquitousVeiws] 'dbo', 0;
CREATE PROCEDURE [adm].[usp_CreateAllUbiquitousVeiws]

	@DictatedTableSchema nvarchar(50) = 'dbo',
	@IsDebug bit = 1

AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY

	IF (@TypeCode NOT IN ('REF','PAR')) BEGIN
		RAISERROR ('Creation of managed foreign keys requires @Typecode (currently "REF" or "PAR").', 16 , -1);
	END

	SET @TableSchema = REPLACE(REPLACE(LTRIM(RTRIM(@TableSchema)), '[', ''), ']', '')
	SET @TableName = REPLACE(REPLACE(LTRIM(RTRIM(@TableName)), '[', ''), ']', '')
	SET @ColumnName = REPLACE(REPLACE(LTRIM(RTRIM(@ColumnName)), '[', ''), ']', '')
	SET @ForeignTableSchema = REPLACE(REPLACE(LTRIM(RTRIM(@ForeignTableSchema)), '[', ''), ']', '')
	SET @ForeignTableName = REPLACE(REPLACE(LTRIM(RTRIM(@ForeignTableName)), '[', ''), ']', '')
	SET @ForeignColumnName = REPLACE(REPLACE(LTRIM(RTRIM(@ForeignColumnName)), '[', ''), ']', '')

	-- Create the Foreign Key
	DECLARE @CascadeDeleteStr nvarchar(20) = CASE (@IsCascadeDelete) WHEN 1 THEN ' ON DELETE CASCADE' ELSE '' END;
	DECLARE	@Sql nvarchar(4000);
	SET @Sql = 'ALTER TABLE [' + @TableSchema + '].[' + @TableName + '] 
				ADD CONSTRAINT [FK_' + @TypeCode + '_' + @TableSchema + @TableName + '_' + @ColumnName + '_' + @ForeignTableSchema + @ForeignTableName +'_' + @ForeignColumnName + '] 
				FOREIGN KEY ([' + @ColumnName +']) REFERENCES [' + @ForeignTableSchema + '].[' + @ForeignTableName +'] ([' + @ForeignColumnName + '])' + @CascadeDeleteStr + ';'
	--PRINT @Sql;
	EXEC sp_ExecuteSql @Sql;

	-- Create the Index to support the ForeignKey.
	SET @Sql = 'CREATE NONCLUSTERED INDEX IX_' + @TableSchema + @TableName + '_' + @ColumnName + ' ON [' + @TableSchema + '].[' + @TableName + ']([' + @ColumnName + '] ASC);'
	--PRINT @Sql;
	EXEC sp_ExecuteSql @Sql;

EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- =============================================
-- Author:		Markus Schippel
-- Create date: 2024-06-21
-- Description:	Create a Foreign Key along with its Non-Clustered Index.
-- =============================================
-- EXEC [adm].[usp_CreateForeignKey] 'dbo', 'DbConnection', 'Col01', 'dbo', 'Context';
CREATE PROCEDURE [adm].[usp_CreateForeignKey]

	@TableSchema nvarchar(20),
	@TableName nvarchar(100),
	@ColumnName nvarchar(100),
	@ForeignTableSchema nvarchar(20),
	@ForeignTableName nvarchar(100),
	@TypeCode nvarchar(20),
	@IsCascadeDelete bit = 1,
	@ForeignColumnName nvarchar(100) = 'Id'

AS

BEGIN
SET NOCOUNT ON;

	-- Equipment UML:
	PRINT '@startuml';
	EXEC [adm].[usp_GetPlantUml] 'dbo', 'Equipment', 0;
	EXEC [adm].[usp_GetPlantUml] 'dbo', 'EquipmentModel', 0;
	EXEC [adm].[usp_GetPlantUml] 'dbo', 'ActivityPlan', 0;
	EXEC [adm].[usp_GetPlantUml] 'dbo', 'Activity', 0;
	EXEC [adm].[usp_GetPlantUml] 'dbo', 'Company', 0;
	PRINT '@enduml';

END

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY

	SET @ParentTableSchema = REPLACE(REPLACE(LTRIM(RTRIM(@ParentTableSchema)), '[', ''), ']', '')
	SET @ParentTableName = REPLACE(REPLACE(LTRIM(RTRIM(@ParentTableName)), '[', ''), ']', '')
	SET @ChildTableSchema = REPLACE(REPLACE(LTRIM(RTRIM(@ChildTableSchema)), '[', ''), ']', '')
	SET @ChildTableName = REPLACE(REPLACE(LTRIM(RTRIM(@ChildTableName)), '[', ''), ']', '')
	SET @NewTableSchema = REPLACE(REPLACE(LTRIM(RTRIM(@NewTableSchema)), '[', ''), ']', '')
	SET @NewTableName = REPLACE(REPLACE(LTRIM(RTRIM(@NewTableName)), '[', ''), ']', '')

	-- Declare variables.
	DECLARE	@Sql nvarchar(4000),
			@ParentIdDataType nvarchar(8),
			@ChildIdDataType nvarchar(8),
			@ErrMsg nvarchar(255);


	-- Initialize some variables.
	SET @NewTableName = LTRIM(RTRIM(@NewTableName));
	IF(LEN(@NewTableName) < 3) SET @NewTableName = NULL;
	SET @ParentIdDataType = 'INT';
	SET @ChildIdDataType = 'INT';


	-- "Clean" passed in arguments.
	SET @ParentTableName = LTRIM(RTRIM(@ParentTableName));
	IF(@ParentTableName = '') SET @ParentTableName = NULL;
	SET @ChildTableName = LTRIM(RTRIM(@ChildTableName));
	IF(@ChildTableName = '') SET @ChildTableName = NULL;


	-- Validate inputs...
	IF( (@ParentTableName IS NULL) OR (@ChildTableName IS NULL) ) BEGIN
		SET @ErrMsg = 'ParentTableName and ChildTableName are required.';
		RAISERROR (@ErrMsg, 16 , -1);
	END


	-- For Lookup table, need to change data type.
	IF( LEFT(@ParentTableName, 2) = 'lk') BEGIN
		SET @ParentIdDataType = 'SMALLINT';
	END
	IF( LEFT(@ChildTableName, 2) = 'lk') BEGIN
		SET @ChildIdDataType = 'SMALLINT';
	END


	-- Set NewTableName if one was not passed in.
	IF(@NewTableName IS NULL) BEGIN
		SET @NewTableName = 'jn' + @ParentTableName + @ChildTableName;
	END


	-- Validate Parent and Child table values represent existing tables with the expected identity column.
	CREATE TABLE #CheckIdTbl ([Id] int);
	SET @Sql = '	SELECT	TOP 1 COL.[Id]
					FROM	sysobjects TBL
							INNER JOIN sys.schemas SCH
							 ON TBL.uid = SCH.schema_id
							INNER JOIN syscolumns COL
							 ON TBL.[id] = COL.[id]
					WHERE	TBL.[name] = ''' + @ParentTableName + '''
					  AND	COL.[name] = ''Id''
					  AND	SCH.[name] = ''' + @ParentTableSchema + '''
					  AND	COL.status = 128';
	INSERT INTO #CheckIdTbl	EXEC sp_ExecuteSql @Sql;
	IF( (SELECT COUNT([Id]) FROM #CheckIdTbl) != 1) BEGIN
		SET @ErrMsg = 'Unable to create junction table.  The ParentTable ''' + @ParentTableSchema + '.' + @ParentTableName + ''' does not exist, or does not contain expected identity column ''Id''.';
		RAISERROR (@ErrMsg, 16 , -1);
	END


	TRUNCATE TABLE #CheckIdTbl
	SET @Sql = '	SELECT	TOP 1	COL.[id]
					FROM	sysobjects TBL
							INNER JOIN sys.schemas SCH
							 ON TBL.uid = SCH.schema_id
							INNER JOIN syscolumns COL
							 ON TBL.[id] = COL.[id]
					WHERE	TBL.[name] = ''' + @ChildTableName + '''
					  AND	COL.[name] = ''Id''
					  AND	SCH.[name] = ''' + @ChildTableSchema + '''
					  AND	COL.status = 128';
	INSERT INTO #CheckIdTbl EXEC sp_ExecuteSql @Sql;
	IF( (SELECT COUNT([Id]) FROM #CheckIdTbl) != 1) BEGIN
		SET @ErrMsg = 'Unable to create junction table. The ChildTable ''' + @ChildTableSchema + '.' + @ChildTableName + ''' does not exist, or does not contain expected identity column ''Id''.';
		RAISERROR (@ErrMsg, 16 , -1);
	END
	DROP TABLE #CheckIdTbl;


	-- Create the table.
	SET @Sql = 'CREATE TABLE [' + @NewTableSchema + '].[' + @NewTableName + '] (
		[Id] INT IDENTITY (1, 1) NOT NULL ,
		[' + @ParentTableName + 'Id] [' + @ParentIdDataType + '] NOT NULL ,
		[' + @ChildTableName + 'Id] [' + @ChildIdDataType + '] NOT NULL,
		[DateCreated] DATETIMEOFFSET (7) CONSTRAINT [DF_' + @NewTableSchema + @NewTableName + '_DateCreated] DEFAULT (sysdatetimeoffset()) NOT NULL,
		[LastUpdateBy] NVARCHAR (50) CONSTRAINT [DF_' + @NewTableSchema + @NewTableName + '_LastUpdateBy] DEFAULT (''{system}'') NOT NULL,
		[DateDeactivated] DATETIMEOFFSET (7) NULL,
		CONSTRAINT [PK_' + @NewTableSchema + @NewTableName + '] PRIMARY KEY CLUSTERED ([' + @ParentTableName + 'Id] ASC, [' + @ChildTableName + 'Id] ASC)
	) ON [PRIMARY]';
	EXEC sp_ExecuteSql @Sql;


	-- Create its Foreign Keys.
	DECLARE	@PointerToParentTable nvarchar(100) = @ParentTableName + 'Id',
			@PointerToChildTable nvarchar(100) = @ChildTableName + 'Id';
	EXEC [adm].[usp_CreateForeignKey] @NewTableSchema, @NewTableName, @PointerToParentTable, @ParentTableSchema, @ParentTableName, 'PAR';
	EXEC [adm].[usp_CreateForeignKey] @NewTableSchema, @NewTableName, @PointerToChildTable, @ChildTableSchema, @ChildTableName, 'PAR';


EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- =============================================
-- Author:		Markus Schippel
-- Create date: 2024-01-22
-- Description:	Create a join (junction) table.
-- =============================================
-- EXEC [adm].[usp_CreateTable_Join] 'dbo', 'Company', 'dbo', 'Address', 'dbo';
CREATE PROCEDURE [adm].[usp_CreateTable_Join]

	@ParentTableSchema nvarchar(20),
	@ParentTableName nvarchar(100),
	@ChildTableSchema nvarchar(20),
	@ChildTableName nvarchar(100),
	@NewTableSchema nvarchar(20),
	@NewTableName nvarchar(150) = null

AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY

	-- Declare variables.
	DECLARE	@Sql nvarchar(4000),
			@FullName nvarchar(100),
			@ErrMsg nvarchar(255);


	-- Initialize variables.
	SET @FullName = 'lk' + @BaseTableName;


	-- "Clean" passed in arguments.
	SET @BaseTableName = LTRIM(RTRIM(@BaseTableName));
	IF(@BaseTableName = '') SET @BaseTableName = NULL;


	-- Validate inputs...
	IF( @BaseTableName IS NULL ) BEGIN
		SET @ErrMsg = 'BaseTableName is required.';
		RAISERROR (@ErrMsg, 16 , -1);
	END
	IF(LEFT(@BaseTableName, 2) = 'lk') BEGIN
		SET @ErrMsg = 'Do not prepend ''lk'' to BaseTableName parameter.';
		RAISERROR (@ErrMsg, 16 , -1);
	END


	-- Create the table.
	-- Note:	No DateCreated/DateDeactivated field - lookups (enums/fixed-lists) are considered configuration, 
	--			if created/deleted info is needed, look in DbAudit.
	SET @Sql = 'CREATE TABLE [' + @TableSchema + '].[' + @FullName+ '] (
		[Id]				SMALLINT				IDENTITY (1, 1)	NOT NULL,
		[Code]				NVARCHAR (10)			NOT NULL,
		[Name]				NVARCHAR (50)			NOT NULL,
		[Description]		NVARCHAR (255)			NULL,
		[DateCreated]       DATETIMEOFFSET (7)		CONSTRAINT [DF_' + @TableSchema + @FullName + '_DateCreated] DEFAULT (sysdatetimeoffset())	NOT NULL,
		[LastUpdateBy]      NVARCHAR (50)			CONSTRAINT [DF_' + @TableSchema + @FullName + '_LastUpdateBy] DEFAULT (''{system}'')		NOT NULL,
		[DateDeactivated]   DATETIMEOFFSET (7)		NULL,
		CONSTRAINT [PK_' + @TableSchema + @FullName + '] PRIMARY KEY CLUSTERED ([Id] ASC),
		CONSTRAINT [U_' +  @TableSchema + @FullName+ '_Code] UNIQUE NONCLUSTERED ([Code]),
		CONSTRAINT [U_' +  @TableSchema + @FullName+ '_Name] UNIQUE NONCLUSTERED ([Name])
	) ON [PRIMARY]';
	EXEC sp_ExecuteSql @Sql;


	-- Create its "LookupCheck" Trigger.
	--EXEC [adm].[usp_CreateLookupCheckTrigger] @FullName, @TableSchema;


	-- Create its Audit Trigger.
	--EXEC [adm].[usp_CreateTableAuditTrigger] @FullName, @TableSchema;

END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- =============================================
-- Author:		Markus Schippel
-- Create date: 2024-05-30
-- Description:	Create a lookup table.
-- =============================================
-- EXEC [adm].[usp_CreateTable_Lookup] 'ContextType1';
CREATE PROCEDURE [adm].[usp_CreateTable_Lookup]

	@BaseTableName nvarchar(100),
	@TableSchema nvarchar(20) = 'dbo'

AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY

	-- Declare Locals.
	DECLARE	@Sql nvarchar(2000);

	-- Drop the Trigger if it already exists.
	SET @Sql = 'IF EXISTS (SELECT * FROM [dbo].[sysobjects] WHERE id = object_id(N''[' + @TableSchema + '].[Trigger_TableAudit_' + @TableSchema + @TableName + ']'') AND OBJECTPROPERTY(id, N''IsTrigger'') = 1) BEGIN
					DROP TRIGGER [' + @TableSchema + '].[Trigger_TableAudit_' + @TableSchema + @TableName + '];
				END';
	EXEC sp_executesql @Sql;


	-- Create "TableAudit" Trigger for current table.
	-- NOTE:  DO NOT reformat the sql string below...
	SET @Sql = 'CREATE TRIGGER [' + @TableSchema + '].[Trigger_TableAudit_' + @TableSchema + @TableName + '] ON [' + @TableSchema + '].[' + @TableName + ']
FOR INSERT, UPDATE, DELETE AS
SET NOCOUNT ON

	DECLARE @TableName sysname = (SELECT CONCAT(''['', OBJECT_SCHEMA_NAME([parent_id]), ''].['', OBJECT_NAME([parent_id]), '']'') FROM [sys].[triggers] WHERE OBJECT_ID = @@PROCID);
	DECLARE @Json nvarchar(max) = (	SELECT [Deleted]=(SELECT * FROM [Deleted] FOR JSON AUTO), [Inserted]=(SELECT * FROM [Inserted] FOR JSON AUTO) FOR JSON PATH, WITHOUT_ARRAY_WRAPPER );
	EXEC [adm].[usp_ManageTableAudit] @TableName, @Json;';
	EXEC sp_ExecuteSql @Sql;

EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- ============================================= -- Author: Markus Schippel -- Create date: 2024-03-06 -- Description: Used to create the TableAudit trigger for the passed in table. -- An INSERT, UPDATE, DELETE trigger will be created. -- ============================================= -- [adm].[usp_CreateTableAuditTrigger] 'Address' CREATE PROCEDURE [adm].[usp_CreateTableAuditTrigger]
@TableName nvarchar(100), @TableSchema nvarchar(20) = 'dbo'
AS

BEGIN
SET NOCOUNT ON;

	IF (@ParentJoinTableSchema IS NULL) SET @ParentJoinTableSchema = 'dbo';

	DECLARE @HasDateDeactivated bit = 0;

	-- Construct name of ubiquitous view.
	DECLARE	@ViewName nvarchar(500) = CONCAT('[', @TableSchema, '].[v_', @TableName, ']') 

	DECLARE @JnColumnSql nvarchar(500)='', @JnFromSql nvarchar(1000)='', @JnJoinSql nvarchar(1000)='';
	IF (@ParentJoinTableName IS NOT NULL) BEGIN

		-- Collect values from join table columns.
		DECLARE @JnTableSchema nvarchar(255), @JnTableName nvarchar(255), @ParentColumnName nvarchar(255), @ChildColumnName nvarchar(255), 
				@JnFkTableSchema nvarchar(255), @JnFkTableName nvarchar(255), @JnFkTableColumn nvarchar(255);
		SELECT	@JnTableSchema=[TableSchema], @JnTableName=[TableName], @ParentColumnName=[ColumnName] 
		FROM	[adm].[vDbColumnInfo] 
		WHERE	[TableSchema] = @ParentJoinTableSchema 
		  AND	[TableName] = @ParentJoinTableName
		  AND	[ColumnId] = 2;
		SELECT	@ChildColumnName=[ColumnName], @JnFkTableSchema=[FkTableSchema], @JnFkTableName=[FkTableName], @JnFkTableColumn=[FkTableColumn] 
		FROM	[adm].[vDbColumnInfo] 
		WHERE	[TableSchema] = @ParentJoinTableSchema 
		  AND	[TableName] = @ParentJoinTableName
		  AND	[ColumnId] = 3;

		-- Create join Sql to inject below.
		SET @JnColumnSql = CONCAT('JN.[', @ParentColumnName, '], 
			');
		SET @JnFromSql = CONCAT('[', @JnTableSchema, '].[', @JnTableName, '] JN 
			INNER JOIN ');
		SET @JnJoinSql = CONCAT('ON JN.[', @ChildColumnName, '] = TBL.[', @JnFkTableColumn, '] ');

		-- Change view name if has a parent join table.
		SET @ViewName = CONCAT('[', @ParentJoinTableSchema, '].[v_', @TableName, '_Via', SUBSTRING(@ParentJoinTableName, 3, (LEN(@ParentJoinTableName)-2)-LEN(@TableName)), ']') 
	END

	DECLARE	@ColumnId int, @ColumnName nvarchar(255), @FkTableSchema nvarchar(255), 
			@FkTableName nvarchar(255), @FkTableColumn nvarchar(255),
			@RowCnt int=-1, @ColSql nvarchar(4000)='', @FromSql nvarchar(4000), @LkCount int = 0;

	-- Append the Join column first (if there is one).
	SET @ColSql = CONCAT(@ColSql, @JnColumnSql);

	-- Delete view if it exists.
	DECLARE @ViewSql nvarchar(4000) = CONCAT('DROP VIEW IF EXISTS ', @ViewName, ';');
	IF (@IsDebug = 1) PRINT @ViewSql;
	IF (@IsDebug = 0) EXECUTE sp_executesql @ViewSql;

	-- Start the From SQL.
	SET @FromSql = CONCAT('
	FROM	', @JnFromSql, '[', @TableSchema + '].[', @TableName, '] TBL ', @JnJoinSql);

	DECLARE EACHROW_CURSOR CURSOR READ_ONLY
	FOR		SELECT  [ColumnId], [ColumnName], [FkTableSchema], [FkTableName], [FkTableColumn]
			FROM	[adm].[vDbColumnInfo]
			WHERE	[TableSchema] = @TableSchema 
			  AND	[TableName] = @TableName
			ORDER BY [ColumnId];
	OPEN EACHROW_CURSOR
	FETCH NEXT FROM EACHROW_CURSOR INTO @ColumnId, @ColumnName, @FkTableSchema, @FkTableName, @FkTableColumn
	WHILE (@@fetch_status <> -1)
	BEGIN
		--PRINT CONCAT(@ColumnId, ' ', @ColumnName, ' ', @FkTableSchema, ' ', @FkTableName, ' ', @FkTableColumn);

		IF (@ColumnName = 'DateDeactivated') BEGIN
			SET @HasDateDeactivated = 1;
			GOTO ENDLOOP;
		END
		SET @RowCnt = @RowCnt+1;
		SET @ColSql = CONCAT(@ColSql, (CASE WHEN (@RowCnt > 0) THEN ', ' ELSE '' END));
		IF ((@RowCnt > 0) AND (@RowCnt % 5) = 0) SET @ColSql = @ColSql + ' 
			';

		-- Add Column.
		IF (@ColumnName = 'Id') BEGIN
			SET @ColSql = CONCAT(@ColSql, '[', @TableName, @ColumnName,']=TBL.[', @ColumnName, ']');
		END ELSE BEGIN
			SET @ColSql = CONCAT(@ColSql, 'TBL.[', @ColumnName, ']');
		END

		IF (LEFT(@FkTableName, 2) = 'lk') BEGIN
			SET @LkCount=@LkCount+1;
			SET @RowCnt = @RowCnt+1;
			SET @ColSql = CONCAT(@ColSql, ', ');
			IF ((@RowCnt > 0) AND (@RowCnt % 5) = 0) SET @ColSql = @ColSql + ' 
			';
			SET @ColSql = CONCAT(@ColSql, '[', SUBSTRING(@ColumnName, 0, LEN(@ColumnName)-1), 'Code]=LK', + @LkCount, '.[Code]');
			SET @RowCnt = @RowCnt+1;
			SET @ColSql = CONCAT(@ColSql, ', ');
			IF ((@RowCnt > 0) AND (@RowCnt % 5) = 0) SET @ColSql = @ColSql + ' 
			';
			SET @ColSql = CONCAT(@ColSql, '[', SUBSTRING(@ColumnName, 0, LEN(@ColumnName)-1), ']=LK', + @LkCount, '.[Name]');

			SET @FromSql = CONCAT(@FromSql, '
			LEFT OUTER JOIN [', @FkTableSchema, '].[', @FkTableName, '] LK', + @LkCount, ' ON TBL.[', @ColumnName, '] = LK', + @LkCount, '.[', @FkTableColumn, ']');
		END

		ENDLOOP:
		FETCH NEXT FROM EACHROW_CURSOR INTO @ColumnId, @ColumnName, @FkTableSchema, @FkTableName, @FkTableColumn
	END
	CLOSE EACHROW_CURSOR; DEALLOCATE EACHROW_CURSOR

	-- Create the view SQL.
	SET @ViewSql = CONCAT('CREATE VIEW ', @ViewName, ' AS 
	SELECT	');
	SET @ViewSql = CONCAT(@ViewSql, @ColSql);
	SET @ViewSql = CONCAT(@ViewSql, @FromSql);
	IF (@HasDateDeactivated = 1) BEGIN
		SET @ViewSql = CONCAT(@ViewSql, '
	WHERE	TBL.[DateDeactivated] IS NULL;');
	END
	IF (@IsDebug = 1) PRINT @ViewSql;
	IF (@IsDebug = 0) BEGIN
		EXECUTE sp_executesql @ViewSql;
		PRINT CONCAT('Created (or replaced) ubiquitous view ''', @ViewName, '''.');
	END

END

-- ==========================================================================================
-- Author:		Markus Schippel
-- Create date: 2024-04-04
-- Description:	Used to generate a standard (ubiquitous) table view. For now, this view simply 
--				enhances enum information from its table with the enum's string representations 
--				and returns	only active records.
--				Note: If the view exists, it will be replaced.
-- ==========================================================================================
-- [adm].[usp_CreateUbiquitousVeiw] 'dbo', 'jnProductComment', 'dbo', 'Comment';
-- [adm].[usp_CreateUbiquitousVeiw] null, null, 'dbo', 'Product';
CREATE PROCEDURE [adm].[usp_CreateUbiquitousVeiw]

	@ParentJoinTableSchema nvarchar(255),
	@ParentJoinTableName nvarchar(255),
	@TableSchema nvarchar(50), 
	@TableName nvarchar(255),
	@IsDebug bit = 1
	
AS

BEGIN SET NOCOUNT ON;
DECLARE @TableSchema nvarchar(50), @TableName nvarchar(255), @Line nvarchar(1000); DECLARE EACHTABLE_CURSOR CURSOR READ_ONLY FOR SELECT [TableSchema], [TableName] FROM [adm].[vDbColumnInfo] WHERE [ColumnId] = 1 -- Just getting one rec per table. AND [IsLookupTable] = 0 AND [IsJoinTable] = 0 AND (@DictatedTableSchema IS NULL OR @DictatedTableSchema = [TableSchema]) ORDER BY [TableName], [ColumnId]; OPEN EACHTABLE_CURSOR FETCH NEXT FROM EACHTABLE_CURSOR INTO @TableSchema, @TableName WHILE (@@fetch_status <> -1) BEGIN
PRINT 'public virtual DbSet<' + @TableName + '> ' + @TableName + 'Set { get; set; }';
FETCH NEXT FROM EACHTABLE_CURSOR INTO @TableSchema, @TableName END CLOSE EACHTABLE_CURSOR; DEALLOCATE EACHTABLE_CURSOR
PRINT ''; PRINT '';
DECLARE EACHTABLE2_CURSOR CURSOR READ_ONLY FOR SELECT [TableSchema], [TableName] FROM [adm].[vDbColumnInfo] WHERE [ColumnId] = 1 -- Just getting one rec per table. AND [IsLookupTable] = 0 AND [IsJoinTable] = 0 AND (@DictatedTableSchema IS NULL OR @DictatedTableSchema = [TableSchema]) ORDER BY [TableName], [ColumnId]; OPEN EACHTABLE2_CURSOR FETCH NEXT FROM EACHTABLE2_CURSOR INTO @TableSchema, @TableName WHILE (@@fetch_status <> -1) BEGIN
SET @Line = 'EXEC [adm].[usp_GetCSharpEfModelBuilderEntity] ''' + @TableSchema + ''', ''' + @TableName + ''';'; --PRINT @Line; EXECUTE sp_executesql @Line; PRINT '';
FETCH NEXT FROM EACHTABLE2_CURSOR INTO @TableSchema, @TableName END CLOSE EACHTABLE2_CURSOR; DEALLOCATE EACHTABLE2_CURSOR
END

-- ============================================= -- Author: Markus Schippel -- Create date: 2024-07-01 -- Description: Used to generate c# code for Entity Framework ModelBuilder entities for a given schema. -- ============================================= -- [adm].[usp_GetAllCSharpEfModelBuilderEntitiesForSchema] 'dbo' CREATE PROCEDURE [adm].[usp_GetAllCSharpEfModelBuilderEntitiesForSchema]
@DictatedTableSchema nvarchar(50) = 'dbo'
AS

BEGIN
SET NOCOUNT ON;

	DECLARE	@TableSchema nvarchar(50), @TableName nvarchar(255), @Line nvarchar(1000);
	DECLARE EACHTABLE_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[TableSchema], [TableName]
			FROM	[adm].[vDbColumnInfo]
			WHERE	[ColumnId] = 1
			  AND	[IsLookupTable] = 1
			  AND	(@DictatedTableSchema IS NULL OR @DictatedTableSchema = [TableSchema])
			ORDER BY [TableSchema], [TableName];
	OPEN EACHTABLE_CURSOR
	FETCH NEXT FROM EACHTABLE_CURSOR INTO @TableSchema, @TableName
	WHILE (@@fetch_status <> -1)
	BEGIN

		SET @Line = 'EXEC [adm].[usp_GetCSharpEnum] ''' + @TableSchema + ''', ''' + @TableName + ''';';
		--PRINT @Line;
		EXECUTE sp_executesql @Line;
		PRINT '';

		FETCH NEXT FROM EACHTABLE_CURSOR INTO @TableSchema, @TableName
	END
	CLOSE EACHTABLE_CURSOR DEALLOCATE EACHTABLE_CURSOR

	-- EXEC [adm].[usp_GetCSharpEnum] 'dbo', 'lkAddressType';

END

-- ============================================= -- Author: Markus Schippel -- Create date: 2024-02-14 -- Description: Used to generate c# code for enums for a given schema. -- ============================================= -- [adm].[usp_GetAllCSharpEnumsForSchema] 'dbo' CREATE PROCEDURE [adm].[usp_GetAllCSharpEnumsForSchema]
@DictatedTableSchema nvarchar(50) = 'dbo'
AS

BEGIN
SET NOCOUNT ON;

	DECLARE	@TableSchema nvarchar(50), @TableName nvarchar(255), @Line nvarchar(1000);
	DECLARE EACHTABLE_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[TableSchema], [TableName]
			FROM	[adm].[vDbColumnInfo]
			WHERE	[ColumnId] = 1
			  AND	[IsLookupTable] = 0 
			  AND	[IsJoinTable] = 0
			  AND	(@DictatedTableSchema IS NULL OR @DictatedTableSchema = [TableSchema])
			ORDER BY [TableSchema], [TableName];
	OPEN EACHTABLE_CURSOR
	FETCH NEXT FROM EACHTABLE_CURSOR INTO @TableSchema, @TableName
	WHILE (@@fetch_status <> -1)
	BEGIN

		SET @Line = 'EXEC [adm].[usp_GetCSharpModel] ''' + @TableSchema + ''', ''' + @TableName + ''';';
		--PRINT @Line;
		EXECUTE sp_executesql @Line;
		PRINT '';

		FETCH NEXT FROM EACHTABLE_CURSOR INTO @TableSchema, @TableName
	END
	CLOSE EACHTABLE_CURSOR DEALLOCATE EACHTABLE_CURSOR;

	-- EXEC [adm].[usp_GetCSharpModel] 'dbo', 'Address';

END

-- ============================================= -- Author: Markus Schippel -- Create date: 2024-02-14 -- Description: Used to generate c# code for models for a given schema. -- ============================================= -- [adm].[usp_GetAllCSharpModelsForSchema] 'dbo' CREATE PROCEDURE [adm].[usp_GetAllCSharpModelsForSchema]
@DictatedTableSchema nvarchar(50) = 'dbo'
AS

BEGIN
SET NOCOUNT ON;

	PRINT '@startuml';

	DECLARE	@TableSchema nvarchar(50), @TableName nvarchar(255), @Line nvarchar(1000);
	DECLARE EACHTABLE_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[TableSchema], [TableName]
			FROM	[adm].[vDbColumnInfo]
			WHERE	[ColumnId] = 1
			  AND	[IsLookupTable] = 0 
			  AND	[IsJoinTable] = 0
			  AND	(@DictatedTableSchema IS NULL OR @DictatedTableSchema = [TableSchema])
			ORDER BY [TableSchema], [TableName];
	OPEN EACHTABLE_CURSOR
	FETCH NEXT FROM EACHTABLE_CURSOR INTO @TableSchema, @TableName
	WHILE (@@fetch_status <> -1)
	BEGIN

		SET @Line = '[adm].[usp_GetPlantUmlRelationshipsForEntity] ''' + @TableSchema + ''', ''' + @TableName + ''';';
		--PRINT @Line;
		EXECUTE sp_executesql @Line;
		PRINT '';

		FETCH NEXT FROM EACHTABLE_CURSOR INTO @TableSchema, @TableName
	END
	CLOSE EACHTABLE_CURSOR DEALLOCATE EACHTABLE_CURSOR;

	PRINT '@enduml';

END

-- ============================================= -- Author: Markus Schippel -- Create date: 2024-02-14 -- Description: Used to generate c# code for models for a given schema. -- ============================================= -- [adm].[usp_GetAllPlantUmlRelationships] 'dbo' CREATE PROCEDURE [adm].[usp_GetAllPlantUmlRelationships]
@DictatedTableSchema nvarchar(50) = 'dbo'
AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY

	DECLARE @Schema nvarchar(50) = (SELECT schema_name([schema_id]) FROM sys.objects WHERE schema_name([schema_id]) = @TableSchema AND [name] = @TableName)

	DECLARE	@IndexName nvarchar(255) = (SELECT [IndexName] FROM [adm].[vDbIndexInfo] where [TableName] = @TableName AND [IsPrimaryKey] = 1);
	DECLARE	@IndexColumnName nvarchar(255) = REPLACE(REPLACE((SELECT [Columns] FROM [adm].[vDbIndexInfo] where [TableName] = @TableName AND [IsPrimaryKey] = 1), '[', ''), ']', '');

	PRINT	'';
	PRINT	'// public virtual DbSet<' + @TableName + '> ' + @TableName + 'Set { get; set; }';
	PRINT	'modelBuilder.Entity<' + @TableName + '>(entity =>';
	PRINT	'{';
	PRINT	'	entity.HasKey(e => e.' + @IndexColumnName + ').HasName("' + @IndexName + '");';
	PRINT	'	entity.ToTable("' + @TableName + '");';
	PRINT	'	entity.ToTable(e => e.UseSqlOutputClause(false)); //needed since EF code prohibits triggers by default';

	DECLARE	@IsUnique bit, @IsUniqueStr nvarchar(11);
	DECLARE DBINDEX_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[IndexName], [Columns], [IsUnique]
			FROM	[adm].[vDbIndexInfo]
			WHERE	[TableSchema] = @TableSchema
			  AND	[TableName] = @TableName
			  AND	[IsPrimaryKey] = 0;
	OPEN DBINDEX_CURSOR
	FETCH NEXT FROM DBINDEX_CURSOR INTO @IndexName, @IndexColumnName, @IsUnique
	WHILE (@@fetch_status <> -1)
	BEGIN

		SET @IndexColumnName = REPLACE(REPLACE(@IndexColumnName, '[', ''), ']', '');
		SET @IsUniqueStr = CASE WHEN @IsUnique = 1 THEN '.IsUnique()' ELSE '' END;	
		PRINT	'	entity.HasIndex(e => e.' + @IndexColumnName + ', "' + @IndexName + '")' + @IsUniqueStr + ';';

		FETCH NEXT FROM DBINDEX_CURSOR INTO @IndexName, @IndexColumnName, @IsUnique
	END
	CLOSE DBINDEX_CURSOR DEALLOCATE DBINDEX_CURSOR;


	-- Add all property rules.
	DECLARE	@ColumnName nvarchar(255), @DataType nvarchar(255), @EntityDataType nvarchar(255), @EntityFieldName nvarchar(255), @TextMaxLength int, 
			@AllowNull bit, @DefaultValue nvarchar(1000), @FkTableName nvarchar(255), @FkIsLookup bit, @FkType nvarchar(20);
	DECLARE	@CSharpType nvarchar(255), @CSharpField nvarchar(255), @Line nvarchar(1000), @Extra nvarchar(255), @ListsExist bit = 0,
			@SkipAdd bit;
	DECLARE	@IsRequiredStr nvarchar(13), @TextMaxLengthStr nvarchar(50), @IsUnicodeStr nvarchar(17), @DefaultValueStr nvarchar(1000), @IsFixedLengthStr nvarchar(16);
	DECLARE BASETBL_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[ColumnName], [DataType], [EntityDataType], [EntityFieldName], [TextMaxLength], [AllowNull], 
					[DefaultValue], [FkTableName], [FkIsLookup], [FkType]
			FROM	[adm].[vDbColumnInfo]
			WHERE	[TableSchema] = @TableSchema
			  AND	[TableName] = @TableName
			ORDER BY [TableName], [ColumnId]
	OPEN BASETBL_CURSOR
	FETCH NEXT FROM BASETBL_CURSOR INTO @ColumnName, @DataType, @EntityDataType, @EntityFieldName, @TextMaxLength, 
										@AllowNull, @DefaultValue, @FkTableName, @FkIsLookup, @FkType
	WHILE (@@fetch_status <> -1)
	BEGIN

		IF (@FkTableName IS NULL) BEGIN --skip foreign key fields
			SET @IsRequiredStr = CASE @AllowNull WHEN 1 THEN '' ELSE '.IsRequired()' END;
			SET @TextMaxLengthStr = CASE @TextMaxLength WHEN NULL THEN '' ELSE '.HasMaxLength(' + CAST(@TextMaxLength as nvarchar(20)) + ')' END;
			SET @IsUnicodeStr = CASE WHEN ((@DataType LIKE '%char%' OR @DataType LIKE '%text%') AND LEFT(@DataType, 1)!='n') THEN '.IsUnicode(false)' ELSE '' END;
			SET @DefaultValueStr = CASE @DefaultValue WHEN NULL THEN '' ELSE '.HasDefaultValueSql("' + @DefaultValue + '")' END;
			SET @IsFixedLengthStr = CASE WHEN (@DataType LIKE '%char%' AND @DataType NOT LIKE '%var%' AND LEFT(@DataType, 1)='n') THEN '.IsFixedLength()' ELSE '' END;
			PRINT CONCAT('	entity.Property(e => e.', @EntityFieldName, ')', @IsRequiredStr, @TextMaxLengthStr, @IsUnicodeStr, @DefaultValueStr, @IsFixedLengthStr, ';');
		END

		FETCH NEXT FROM BASETBL_CURSOR INTO @ColumnName, @DataType, @EntityDataType, @EntityFieldName, @TextMaxLength, 
											@AllowNull, @DefaultValue, @FkTableName, @FkIsLookup, @FkType
	END
	CLOSE BASETBL_CURSOR DEALLOCATE BASETBL_CURSOR;

	IF (EXISTS (SELECT [ColumnName] FROM [adm].[vDbColumnInfo] WHERE [TableSchema] = @TableSchema AND [TableName] = @TableName AND[ColumnName] = 'DateDeactivated')) BEGIN
		PRINT '	entity.HasQueryFilter(x => x.DateDeactivated == null); //soft-delete';
	END

	PRINT	'});';


	-- Add child entity lists using direct reference.
	DECLARE	@DirectChildTable nvarchar(255), @DirectChildColumn nvarchar(255), @DirectChildEntityFieldName nvarchar(255), @DirectChildFKType nvarchar(50);
	DECLARE	@DirectChildField nvarchar(255);
	DECLARE DIRECTREF_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[TableName], [ColumnName], [EntityFieldName], [FkType]
			FROM	[adm].[vDbColumnInfo]
			WHERE	[IsJoinTable] = 0
			  AND	[FkTableName] = @TableName
			  AND	[FkTableColumn] = 'Id'
	OPEN DIRECTREF_CURSOR
	FETCH NEXT FROM DIRECTREF_CURSOR INTO @DirectChildTable, @DirectChildColumn, @DirectChildEntityFieldName, @DirectChildFKType
	WHILE (@@fetch_status <> -1)
	BEGIN

		PRINT	'modelBuilder.Entity<' + @TableName + '>()';
		IF (@DirectChildFKType = 'Reference') BEGIN
			PRINT	'.HasMany<' + @DirectChildTable + '>() //(reference-to)'; -- no list property on referenced entity
		END ELSE BEGIN
			PRINT	'.HasMany(c => c.' + @DirectChildTable + 'List) //(child-list)';
		END
		PRINT	'.WithOne(p => p.' + @DirectChildEntityFieldName + ')';
		PRINT	'.HasForeignKey("' + @DirectChildColumn + '")';
		PRINT	'.IsRequired();';

		FETCH NEXT FROM DIRECTREF_CURSOR INTO @DirectChildTable, @DirectChildColumn, @DirectChildEntityFieldName, @DirectChildFKType
	END
	CLOSE DIRECTREF_CURSOR DEALLOCATE DIRECTREF_CURSOR;


	-- Add child entity lists using jn tables.
	DECLARE	@ChildTableSchema nvarchar(50), @ChildTableName nvarchar(255), @JnTableName nvarchar(255)
	DECLARE @JnFieldName nvarchar(255), @NeedJnTblName bit, @Corrections nvarchar(1000);
	DECLARE CHILDOBJECT_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[ChildTableSchema]=[FkTableSchema], [ChildTableName]=[FkTableName], [JnTableName]=[TableName]
			FROM	[adm].[vDbColumnInfo] PAR
					INNER JOIN (	SELECT	[CTableSchema]=[TableSchema], [CTableName]=[TableName]
									FROM	[adm].[vDbColumnInfo]
									WHERE	[IsJoinTable] = 1
									  AND	[ColumnId] = 2
									  AND	[FkTableName] = @TableName) CHD ON PAR.[TableSchema] = CHD.[CTableSchema] AND PAR.[TableName] = CHD.[CTableName]
			WHERE	[ColumnId] = 3
			ORDER BY [FkTableSchema], [FkTableName];
	OPEN CHILDOBJECT_CURSOR
	FETCH NEXT FROM CHILDOBJECT_CURSOR INTO @ChildTableSchema, @ChildTableName, @JnTableName
	WHILE (@@fetch_status <> -1)
	BEGIN

		SET @JnFieldName = @JnTableName;
		IF (LEFT(@JnFieldName, 2) = 'jn' COLLATE Latin1_General_CS_AS) SET @JnFieldName = SUBSTRING(@JnFieldName, 3, 9999);
		IF (LEFT(@JnFieldName, LEN(@TableName)) = @TableName) SET @JnFieldName = SUBSTRING(@JnFieldName, LEN(@TableName)+1, 9999);
		IF (LEFT(@JnFieldName, 2) = 'lk' COLLATE Latin1_General_CS_AS) SET @JnFieldName = SUBSTRING(@JnFieldName, 3, 9999);

		PRINT	'modelBuilder.Entity<' + @TableName + '>()';
		PRINT	'.HasMany(e => e.' + @JnFieldName + 'List)';
		PRINT	'.WithMany()';
		PRINT	'.UsingEntity("' + @JnTableName + '", j =>';
		PRINT	'	{';
		PRINT	'		j.Property(nameof(' + @TableName + ') + "Id").HasColumnName(nameof(' + @TableName + ') + "Id");';
		PRINT	'		j.Property(nameof(' + @TableName + '.' + @JnFieldName + 'List)+"Id").HasColumnName(nameof(' + @ChildTableName + ') + "Id");';
		PRINT	'	});';

		FETCH NEXT FROM CHILDOBJECT_CURSOR INTO @ChildTableSchema, @ChildTableName, @JnTableName
	END
	CLOSE CHILDOBJECT_CURSOR DEALLOCATE CHILDOBJECT_CURSOR;

EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- =============================================
-- Author:		Markus Schippel
-- Create date: 2024-06-21
-- Description:	Creates an EntityFramework ModelBuilder Entity for a table.
-- =============================================
-- EXEC [adm].[usp_GetCSharpEfModelBuilderEntity] 'dbo', 'EquipmentModel';
CREATE PROCEDURE [adm].[usp_GetCSharpEfModelBuilderEntity]

	@TableSchema nvarchar(50),
	@TableName nvarchar(255)

AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY


	IF (LEFT(@TableName, 2) != 'lk') BEGIN
		RAISERROR('Passed in @TableName must begin with "lk".', 16, 1) --change to > 10
		RETURN --exit now
	END

	DECLARE	@EnumName nvarchar(100) = SUBSTRING(@TableName, 3, 255);

	PRINT 'public enum ' + @EnumName + '{';

DECLARE @TSql nvarchar(max) = '
DECLARE @Result nvarchar(max) = '''';
SELECT @Result = @Result + ''
	'' + [ColumnName] + '' = '' + CAST([Id] as nvarchar(20)) + '',''
FROM	(	SELECT	[Id], [ColumnName] = REPLACE(REPLACE([Name], '' '', ''''), '','', '''')
			FROM	[' + @TableSchema + '].[' + @TableName + '] ) LK
ORDER BY [Id];
SET @Result = SUBSTRING(@Result, 3, 10000);
SET @Result = REPLACE(@Result, ''.'', '''');
SET @Result = REPLACE(@Result, ''-'', '''');
SET @Result = REPLACE(@Result, ''_'', '''');
SET @Result = REPLACE(@Result, ''/'', '''');
SET @Result = REPLACE(@Result, ''\'', '''');
SET @Result = REPLACE(@Result, ''&'', '''');
SET @Result = REPLACE(@Result, ''('', '''');
SET @Result = REPLACE(@Result, '')'', '''');
SET @Result = REPLACE(@Result, ''+'', '''');
PRINT @Result;';
EXEC sp_executesql @TSql;

	PRINT '}';


EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- =============================================
-- Author:		Markus Schippel
-- Create date: 2024-02-10
-- Description:	Creates an enum for a lookup table.
-- =============================================
-- EXEC [adm].[usp_GetCSharpEnum] 'dbo', 'lkDonorType';
CREATE PROCEDURE [adm].[usp_GetCSharpEnum]

	@TableSchema nvarchar(50),
	@TableName nvarchar(255)

AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY

	DECLARE @BaseClass nvarchar(50) =	
		CASE
			WHEN EXISTS ((SELECT [ColumnName] FROM [adm].[vDbColumnInfo] WHERE [TableSchema] = @TableSchema AND [TableName] = @TableName AND [ColumnName] = 'FlexData')) THEN 'FlexDataEntityBase'
			WHEN EXISTS ((SELECT [ColumnName] FROM [adm].[vDbColumnInfo] WHERE [TableSchema] = @TableSchema AND [TableName] = @TableName AND [ColumnName] = 'DateDeactivated')) THEN 'TerminatableDataEntityBase'
			ELSE 'DataEntityBase'
		END
	PRINT	'';
	PRINT	'public class ' + @TableName + ' : ' + @BaseClass;
	PRINT	'{';

	-- Add all columns.
	DECLARE	@ColumnName nvarchar(255), @DataType nvarchar(255), @EntityDataType nvarchar(255), @EntityFieldName nvarchar(255), @TextMaxLength int, 
			@IsNullable bit, @FkTableName nvarchar(255), @FkIsLookup bit, @FkType nvarchar(20);
	DECLARE	@Line nvarchar(1000), @ListsExist bit = 0;
	DECLARE BASETBL_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[ColumnName], [DataType], [EntityDataType], [EntityFieldName], [TextMaxLength], [AllowNull], [FkTableName], [FkIsLookup], [FkType]
			FROM	[adm].[vDbColumnInfo] PAR
			WHERE	[TableSchema] = @TableSchema
			  AND	[TableName] = @TableName
			ORDER BY [ColumnId];
	OPEN BASETBL_CURSOR
	FETCH NEXT FROM BASETBL_CURSOR INTO @ColumnName, @DataType, @EntityDataType, @EntityFieldName, @TextMaxLength, @IsNullable, @FkTableName, @FkIsLookup, @FkType
	WHILE (@@fetch_status <> -1)
	BEGIN

		IF (@ColumnName NOT IN ('Id', 'DateCreated', 'LastUpdateBy', 'DateDeactivated', 'FlexData')) BEGIN

			SET @Line = CONCAT('	public ', @EntityDataType, (CASE WHEN @IsNullable = 1 THEN '?' ELSE '' END), ' ', @ColumnName, ' { get; set; }');

			IF (@FkTableName IS NOT NULL) BEGIN
				SET @EntityDataType = @FkTableName;
				IF (LEFT(@EntityDataType, 2) = 'lk' COLLATE Latin1_General_CS_AS) SET @EntityDataType = SUBSTRING(@EntityDataType, 3, 9999);
				SET @Line = @Line + CONCAT(char(13), '	' + '//public ', @EntityDataType, (CASE WHEN @IsNullable = 1 THEN '?' ELSE '' END), ' ', @EntityFieldName, ' { get; set; }');
			END

			IF (@EntityDataType = 'string') BEGIN
				SET @Line = @Line + CONCAT(' //(', REPLACE(CAST(@TextMaxLength as nvarchar(10)), '-1', 'max'), ')');
			END

			--IF (@ColumnName != @EntityFieldName) SET @Line = @Line + ' //[' + @ColumnName + ']';

			PRINT @Line;
		END

		FETCH NEXT FROM BASETBL_CURSOR INTO @ColumnName, @DataType, @EntityDataType, @EntityFieldName, @TextMaxLength, @IsNullable, @FkTableName, @FkIsLookup, @FkType
	END
	CLOSE BASETBL_CURSOR DEALLOCATE BASETBL_CURSOR;


	-- Add child entity lists using direct reference.
	DECLARE	@DirectChildTable nvarchar(255), @DirectChildColumn nvarchar(255), @DirectChildField nvarchar(255);
	DECLARE DIRECTREF_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[TableName], [ColumnName]
			FROM	[adm].[vDbColumnInfo]
			WHERE	[IsJoinTable] = 0
			  AND	[FkTableName] = @TableName
			  AND	[FkTableColumn] = 'Id'
			  AND	[FkType] = 'Parent';
	OPEN DIRECTREF_CURSOR
	FETCH NEXT FROM DIRECTREF_CURSOR INTO @DirectChildTable, @DirectChildColumn
	WHILE (@@fetch_status <> -1)
	BEGIN

		IF (@ListsExist = 0) BEGIN PRINT ''; SET @ListsExist = 1; END

		SET @DirectChildField = @DirectChildTable + 'List';

		PRINT '	public IList<' + @DirectChildTable + '> ' + @DirectChildField + ' { get; set; } = new List<' + @DirectChildTable + '>(); // Relationship via "' + @DirectChildColumn + '" field on ' + @DirectChildTable

		FETCH NEXT FROM DIRECTREF_CURSOR INTO @DirectChildTable, @DirectChildColumn
	END
	CLOSE DIRECTREF_CURSOR DEALLOCATE DIRECTREF_CURSOR;


	-- Add child entity lists using jn tables.
	DECLARE	@ChildTableSchema nvarchar(50), @ChildTableName nvarchar(255), @JnTableName nvarchar(255), @JnChildEntityFieldName nvarchar(255);
	DECLARE	@JnFieldName nvarchar(255);
	DECLARE CHILDOBJECT_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[ChildTableSchema]=[FkTableSchema], [ChildTableName]=[FkTableName], [JnTableName]=[TableName], [JnChildEntityFieldName]=[EntityFieldName]
			FROM	[adm].[vDbColumnInfo] PAR
					INNER JOIN (	SELECT	[CTableSchema]=[TableSchema], [CTableName]=[TableName]
									FROM	[adm].[vDbColumnInfo]
									WHERE	[IsJoinTable] = 1
									  AND	[ColumnId] = 2
									  AND	[FkTableName] = @TableName) CHD ON PAR.[TableSchema] = CHD.[CTableSchema] AND PAR.[TableName] = CHD.[CTableName]
			WHERE	[ColumnId] = 3
			ORDER BY [FkTableSchema], [FkTableName];
	OPEN CHILDOBJECT_CURSOR
	FETCH NEXT FROM CHILDOBJECT_CURSOR INTO @ChildTableSchema, @ChildTableName, @JnTableName, @JnChildEntityFieldName
	WHILE (@@fetch_status <> -1)
	BEGIN

		IF (@ListsExist = 0) BEGIN PRINT ''; SET @ListsExist = 1; END

		SET @JnFieldName = @JnTableName + 'List';
		IF (LEFT(@JnFieldName, 2) = 'jn' COLLATE Latin1_General_CS_AS) SET @JnFieldName = SUBSTRING(@JnFieldName, 3, 9999);
		IF (LEFT(@JnFieldName, LEN(@TableName)) = @TableName) SET @JnFieldName = SUBSTRING(@JnFieldName, LEN(@TableName)+1, 9999);
		IF (LEFT(@JnFieldName, 2) = 'lk' COLLATE Latin1_General_CS_AS) SET @JnFieldName = SUBSTRING(@JnFieldName, 3, 9999);

		PRINT '	public IList<' + @JnChildEntityFieldName + '> ' + @JnFieldName + ' { get; set; } = [];	// Relationship via ' + @JnTableName;

		FETCH NEXT FROM CHILDOBJECT_CURSOR INTO @ChildTableSchema, @ChildTableName, @JnTableName, @JnChildEntityFieldName
	END
	CLOSE CHILDOBJECT_CURSOR DEALLOCATE CHILDOBJECT_CURSOR;

	PRINT	'}';

EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- =============================================
-- Author:		Markus Schippel
-- Create date: 2024-06-06
-- Description:	Creates a base model class for a table.
-- =============================================
-- EXEC [adm].[usp_GetCSharpModel] 'dbo', 'ActivityPlan';
-- EXEC [adm].[usp_GetCSharpModel] 'dbo', 'EquipmentModel';
-- EXEC [adm].[usp_GetCSharpModel] 'dbo', 'ContactItem';
CREATE PROCEDURE [adm].[usp_GetCSharpModel]

	@TableSchema nvarchar(50),
	@TableName nvarchar(255)

AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY

	PRINT	'';
	PRINT	'class ' + @TableName;
	PRINT	'{';

	-- Add all columns.
	DECLARE	@ColumnName nvarchar(255), @EntityDataType nvarchar(255), @EntityFieldName nvarchar(255), @TextMaxLength int, @IsNullable bit, 
			@IsIdentity bit, @IsManaged bit, @FkTableSchema nvarchar(255), @FkTableName nvarchar(255), @FkIsLookup bit, @FkType nvarchar(20);
	DECLARE	@Line nvarchar(1000), @Connections nvarchar(2000) = '', @Enums nvarchar(1000) = '', @Designator char(1), @DataTypeDesc nvarchar(255), 
			@NullableStr nvarchar(10), @ListsExist bit = 0;
	DECLARE BASETBL_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[ColumnName], [EntityDataType], [EntityFieldName], [TextMaxLength], [AllowNull], 
					[IsIdentity], [IsManaged], [FkTableSchema], [FkTableName], [FkIsLookup], [FkType]
			FROM	[adm].[vDbColumnInfo] PAR
			WHERE	[TableSchema] = @TableSchema
			  AND	[TableName] = @TableName
			ORDER BY [ColumnId];
	OPEN BASETBL_CURSOR
	FETCH NEXT FROM BASETBL_CURSOR INTO	@ColumnName, @EntityDataType, @EntityFieldName, @TextMaxLength, @IsNullable, 
										@IsIdentity, @IsManaged, @FkTableSchema, @FkTableName, @FkIsLookup, @FkType
	WHILE (@@fetch_status <> -1)
	BEGIN

		SET @Line = '';

		SET @Designator = (	CASE	WHEN @IsIdentity = 1 THEN '~'
									WHEN @IsManaged = 1 THEN '-'
									WHEN @FkTableName IS NOT NULL THEN '#'
									ELSE '+' 
							END);

		SET @DataTypeDesc =	CASE
								WHEN (@EntityDataType = 'string') THEN CONCAT(@EntityDataType, '(', CAST(@TextMaxLength as nvarchar(10)), ')')
								WHEN (@FkTableName IS NOT NULL AND @FkIsLookup = 0) THEN CONCAT('{', @EntityFieldName, '}')
								WHEN (@FkTableName IS NOT NULL AND @FkIsLookup = 1) THEN CONCAT('[', @EntityFieldName, ']')
								ELSE @EntityDataType
							END;
		SET @DataTypeDesc = REPLACE(@DataTypeDesc, '(-1)', '');

		IF (@FkTableName IS NULL OR (@FkTableName IS NOT NULL AND @FkType = 'Reference')) BEGIN
			SET @Line = CONCAT(@Line, '	{field}', @Designator, ' ', @EntityFieldName, ' : ', @DataTypeDesc, (CASE WHEN @IsNullable = 1 THEN '?' ELSE '' END));
			PRINT @Line;
		END

		-- Append to Connections.
		SET @NullableStr = CASE WHEN @IsNullable = 1 THEN ',dashed' ELSE '' END;
		IF (@FkTableName IS NOT NULL) BEGIN
			IF (@FkType = 'Parent') BEGIN
				IF (@IncludeParentConnection = 1) BEGIN
				SET @Connections = @Connections + '
' + @TableName + ' }-[#green,thickness=2' + @NullableStr + ']-o ' + @FkTableName;
				END
			END ELSE IF (@FkIsLookup = 1) BEGIN
				SET @Enums = @Enums + '
enum ' + @EntityFieldName + '{}';
				SET @Connections = @Connections + '
' + @TableName + ' -[#orange]- ' + @EntityFieldName;
			END ELSE BEGIN
				IF (@IsNullable = 1) SET @NullableStr = '[' + REPLACE(@NullableStr, ',', '') + ']'
				SET @Connections = @Connections + '
' + @TableName + ' *-' + @NullableStr + '-* ' + @FkTableName;
			END
		END

		FETCH NEXT FROM BASETBL_CURSOR INTO	@ColumnName, @EntityDataType, @EntityFieldName, @TextMaxLength, @IsNullable, 
											@IsIdentity, @IsManaged, @FkTableSchema, @FkTableName, @FkIsLookup, @FkType
	END
	CLOSE BASETBL_CURSOR DEALLOCATE BASETBL_CURSOR;


	-- Add child entity lists using direct reference.
	DECLARE	@DirectChildTable nvarchar(255), @DirectChildColumn nvarchar(255), @DirectChildField nvarchar(255);
	DECLARE DIRECTREF_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[TableName], [ColumnName]
			FROM	[adm].[vDbColumnInfo]
			WHERE	[IsJoinTable] = 0
			  AND	[FkTableName] = @TableName
			  AND	[FkTableColumn] = 'Id'
			  AND	[FkType] = 'Parent'
			ORDER BY [TableName], [ColumnId];
	OPEN DIRECTREF_CURSOR
	FETCH NEXT FROM DIRECTREF_CURSOR INTO @DirectChildTable, @DirectChildColumn
	WHILE (@@fetch_status <> -1)
	BEGIN

		IF (@ListsExist = 0) BEGIN PRINT '	=='; SET @ListsExist = 1; END

		SET @DirectChildField = @DirectChildTable + 's';
		IF (RIGHT(@DirectChildField, 2) = 'ys') SET @DirectChildField = REVERSE(SUBSTRING(REVERSE(@DirectChildField), 3, 9999)) + 'ies';
		IF (RIGHT(@DirectChildField, 2) = 'ss') SET @DirectChildField = REVERSE(SUBSTRING(REVERSE(@DirectChildField), 3, 9999)) + 'sList';
		PRINT '	#' + @DirectChildField + ': List{' + @DirectChildTable + '}'; -- + ' [via ' + @DirectChildColumn + ' on ' + @DirectChildTable + ']';
				SET @Connections = @Connections + '
' + @TableName + ' *--{ ' + @DirectChildTable;

		FETCH NEXT FROM DIRECTREF_CURSOR INTO @DirectChildTable, @DirectChildColumn
	END
	CLOSE DIRECTREF_CURSOR DEALLOCATE DIRECTREF_CURSOR;


	-- Add child entity lists using jn tables.
	DECLARE @ChildTableSchema nvarchar(50), @ChildTableName nvarchar(255), @JnTableName nvarchar(255), @JnChildEntityFieldName nvarchar(255), @JnChildEntityIsLookup bit
	DECLARE	@JnFieldName nvarchar(255);
	DECLARE JOINTABLE_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[ChildTableSchema]=[FkTableSchema], [ChildTableName]=[FkTableName], [JnTableName]=[TableName], [JnChildEntityFieldName]=[EntityFieldName], [JnChildEntityIsLookup]=[FkIsLookup]
			FROM	[adm].[vDbColumnInfo] PAR
					INNER JOIN (	SELECT	[CTableSchema]=[TableSchema], [CTableName]=[TableName]
									FROM	[adm].[vDbColumnInfo]
									WHERE	[IsJoinTable] = 1
									  AND	[ColumnId] = 2
									  AND	[FkTableName] = @TableName) CHD ON PAR.[TableSchema] = CHD.[CTableSchema] AND PAR.[TableName] = CHD.[CTableName]
			WHERE	[ColumnId] = 3
			ORDER BY [FkTableSchema], [FkTableName];
	OPEN JOINTABLE_CURSOR
	FETCH NEXT FROM JOINTABLE_CURSOR INTO @ChildTableSchema, @ChildTableName, @JnTableName, @JnChildEntityFieldName, @JnChildEntityIsLookup
	WHILE (@@fetch_status <> -1)
	BEGIN

		IF (@ListsExist = 0) BEGIN PRINT '	=='; SET @ListsExist = 1; END

		SET @JnFieldName = @JnTableName + 's';
		IF (LEFT(@JnFieldName, 2) = 'jn' COLLATE Latin1_General_CS_AS) SET @JnFieldName = SUBSTRING(@JnFieldName, 3, 9999);
		IF (LEFT(@JnFieldName, LEN(@TableName)) = @TableName) SET @JnFieldName = SUBSTRING(@JnFieldName, LEN(@TableName)+1, 9999);
		IF (LEFT(@JnFieldName, 2) = 'lk' COLLATE Latin1_General_CS_AS) SET @JnFieldName = SUBSTRING(@JnFieldName, 3, 9999);
		IF (RIGHT(@JnFieldName, 2) = 'ys') SET @JnFieldName = REVERSE(SUBSTRING(REVERSE(@JnFieldName), 3, 9999)) + 'ies';
		IF (RIGHT(@JnFieldName, 2) = 'ss') SET @JnFieldName = REVERSE(SUBSTRING(REVERSE(@JnFieldName), 3, 9999)) + 'sList';
		PRINT '	#' + @JnFieldName + ' List{' + @JnChildEntityFieldName + '}'; -- + ' [via jn]';

		SET @Connections = CONCAT(@Connections, '
', @TableName, ' ', (CASE WHEN @JnChildEntityIsLookup = 1 THEN '' ELSE '*' END), '-', (CASE WHEN @JnChildEntityIsLookup = 1 THEN '[#orange]' ELSE '' END), '-{ ', @JnChildEntityFieldName, ' : ^');

		IF (@JnChildEntityIsLookup = 1) BEGIN
			SET @Enums = @Enums + '
enum ' + @JnChildEntityFieldName + '{}';
		END

		FETCH NEXT FROM JOINTABLE_CURSOR INTO @ChildTableSchema, @ChildTableName, @JnTableName, @JnChildEntityFieldName, @JnChildEntityIsLookup
	END
	CLOSE JOINTABLE_CURSOR DEALLOCATE JOINTABLE_CURSOR;

	PRINT '}';
	IF (LEN(@Enums) > 0) PRINT @Enums;
	IF (LEN(@Connections) > 0) PRINT @Connections;
	print ''

EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- ============================================= -- Author: Markus Schippel -- Create date: 2024-05-31 -- Description: Creates a base model class for a table. -- ============================================= -- EXEC [adm].[usp_GetPlantUml] 'dbo', 'Equipment' -- EXEC [adm].[usp_GetPlantUml] 'dbo', 'EquipmentModel' -- EXEC [adm].[usp_GetPlantUml] 'dbo', 'ActivityPlan', 0 -- EXEC [adm].[usp_GetPlantUml] 'dbo', 'Company'; CREATE PROCEDURE [adm].[usp_GetPlantUml]
@TableSchema nvarchar(50), @TableName nvarchar(255), @IncludeParentConnection bit = 1
AS

BEGIN SET NOCOUNT ON; BEGIN TRANSACTION; BEGIN TRY
-- Add all one-to-one connections. DECLARE @EntityFieldName nvarchar(255), @AllowNull bit, @FkTableName nvarchar(255), @FkIsLookup bit, @FkType nvarchar(20); DECLARE @Connections nvarchar(2000) = '', @Enums nvarchar(1000) = '', @NullableStr nvarchar(10); DECLARE BASETBL_CURSOR CURSOR READ_ONLY FOR SELECT [EntityFieldName], [AllowNull], [FkTableName], [FkIsLookup], [FkType] FROM [adm].[vDbColumnInfo] PAR WHERE [TableSchema] = @TableSchema AND [TableName] = @TableName ORDER BY [ColumnId]; OPEN BASETBL_CURSOR FETCH NEXT FROM BASETBL_CURSOR INTO @EntityFieldName, @AllowNull, @FkTableName, @FkIsLookup, @FkType WHILE (@@fetch_status <> -1) BEGIN
SET @NullableStr = CASE WHEN @AllowNull = 1 THEN ',dashed' ELSE '' END; IF (@FkTableName IS NOT NULL) BEGIN IF (@FkType = 'Parent') BEGIN SET @Enums = @Enums; END ELSE IF (@FkIsLookup = 1) BEGIN SET @Enums = @Enums + ' enum ' + @EntityFieldName + '{}'; SET @Connections = @Connections + ' ' + @TableName + ' -[#orange]- ' + @EntityFieldName; END ELSE BEGIN IF (@AllowNull = 1) SET @NullableStr = '[' + REPLACE(@NullableStr, ',', '') + ']' SET @Connections = @Connections + ' ' + @TableName + ' *-' + @NullableStr + '-* ' + @FkTableName; END END
FETCH NEXT FROM BASETBL_CURSOR INTO @EntityFieldName, @AllowNull, @FkTableName, @FkIsLookup, @FkType END CLOSE BASETBL_CURSOR DEALLOCATE BASETBL_CURSOR;
-- Add connections for child entity lists using direct reference. DECLARE @DirectChildTable nvarchar(255) DECLARE DIRECTREF_CURSOR CURSOR READ_ONLY FOR SELECT [TableName] FROM [adm].[vDbColumnInfo] WHERE [IsJoinTable] = 0 AND [FkTableName] = @TableName AND [FkTableColumn] = 'Id' AND [FkType] = 'Parent' AND [TableName] != @TableName --prevents key to self which is handled in field logic above ORDER BY [TableName], [ColumnId]; OPEN DIRECTREF_CURSOR FETCH NEXT FROM DIRECTREF_CURSOR INTO @DirectChildTable WHILE (@@fetch_status <> -1) BEGIN
SET @Connections = @Connections + ' ' + @TableName + ' *--{ ' + @DirectChildTable;
FETCH NEXT FROM DIRECTREF_CURSOR INTO @DirectChildTable END CLOSE DIRECTREF_CURSOR DEALLOCATE DIRECTREF_CURSOR;
-- Add connections for child entity lists using jn tables. DECLARE @ChildTableSchema nvarchar(50), @JnChildEntityFieldName nvarchar(255), @JnChildEntityIsLookup bit DECLARE @JnFieldName nvarchar(255); DECLARE JOINTABLE_CURSOR CURSOR READ_ONLY FOR SELECT [ChildTableSchema]=[FkTableSchema], [JnChildEntityFieldName]=[EntityFieldName], [JnChildEntityIsLookup]=[FkIsLookup] FROM [adm].[vDbColumnInfo] PAR INNER JOIN ( SELECT [CTableSchema]=[TableSchema], [CTableName]=[TableName] FROM [adm].[vDbColumnInfo] WHERE [IsJoinTable] = 1 AND [ColumnId] = 2 AND [FkTableName] = @TableName) CHD ON PAR.[TableSchema] = CHD.[CTableSchema] AND PAR.[TableName] = CHD.[CTableName] WHERE [ColumnId] = 3 ORDER BY [FkTableSchema], [FkTableName]; OPEN JOINTABLE_CURSOR FETCH NEXT FROM JOINTABLE_CURSOR INTO @ChildTableSchema, @JnChildEntityFieldName, @JnChildEntityIsLookup WHILE (@@fetch_status <> -1) BEGIN
SET @Connections = CONCAT(@Connections, ' ', @TableName, ' ', (CASE WHEN @JnChildEntityIsLookup = 1 THEN '' ELSE '*' END), '-', (CASE WHEN @JnChildEntityIsLookup = 1 THEN '[#orange]' ELSE '' END), '-{ ', @JnChildEntityFieldName, ' : ^');
IF (@JnChildEntityIsLookup = 1) BEGIN SET @Enums = @Enums + ' enum ' + @JnChildEntityFieldName + '{}'; END
FETCH NEXT FROM JOINTABLE_CURSOR INTO @ChildTableSchema, @JnChildEntityFieldName, @JnChildEntityIsLookup END CLOSE JOINTABLE_CURSOR DEALLOCATE JOINTABLE_CURSOR;
IF (LEN(@Enums) > 0) PRINT @Enums; IF (LEN(@Connections) > 0) PRINT @Connections;
EXIT_HANDLER: END TRY BEGIN CATCH IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION; DECLARE @ErrStr nvarchar(4000); EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT; THROW; END CATCH IF ((XACT_STATE())=1) COMMIT TRANSACTION; END

-- ============================================= -- Author: Markus Schippel -- Create date: 2024-06-07 -- Description: Creates a base model class for a table. -- ============================================= -- EXEC [adm].[usp_GetPlantUmlRelationships] 'dbo', 'ActivityPlan' CREATE PROCEDURE [adm].[usp_GetPlantUmlRelationshipsForEntity]
@TableSchema nvarchar(50), @TableName nvarchar(255)
AS

-- ============================================= -- Author: Markus Schippel -- Create date: 2024-01-18 -- Description: Handle errors. Called from 'CATCH' blocks. -- ============================================= CREATE PROCEDURE [adm].[usp_HandleError]
@CallingProcId bigint, @ErrorInfoStr nvarchar(max) OUTPUT
AS

BEGIN
SET NOCOUNT ON;


	/*
	DROP TABLE [adm].[ForeignKeyDefinitions_bak];
	SELECT * INTO [adm].[ForeignKeyDefinitions_bak] FROM [adm].[ForeignKeyDefinitions];
	*/

	DECLARE @Now datetime = GETDATE();
	IF(@Directive IS NULL) SET @Directive = '';


	-- If it is missing, create the ForeignKeyDefinitions table needed to store ForeignKeys.
	-- CREATE SCHEMA adm;
	IF (NOT EXISTS(SELECT 1 FROM dbo.sysobjects WHERE id = OBJECT_ID(N'[adm].[ForeignKeyDefinitions]'))) BEGIN
		CREATE TABLE [adm].[ForeignKeyDefinitions](
			[Id] int IDENTITY(1,1) NOT NULL,
			[DateCreated] datetime NOT NULL CONSTRAINT [DF_admForeignKeyDefinitions_CreateDate] DEFAULT (getdate()),
			[ParentSchema] nvarchar(20) NOT NULL,
			[ParentTable] nvarchar(100) NOT NULL,
			[ParentColumn] nvarchar(100) NOT NULL,
			[ReferencedSchema] nvarchar(20) NOT NULL,
			[ReferencedTable] nvarchar(100) NOT NULL,
			[ReferencedColumn] nvarchar(100) NOT NULL
		) ON [PRIMARY]
	END


	-- Pervent some logic inconsistencies.
	IF(@RemoveAllForeignKeys = 1 AND @ReAddAllForeignkeys = 1) BEGIN
		PRINT 'Error:  Parameters @RemoveAllForeignKeys and @ReAddAllForeignkeys can''t both be set to ''TRUE'' (can''t both remove and add all foreign keys).';
		GOTO EXIT_STORED_PROCEDURE;
	END


	-- Pre-Execution Statistics.
	DECLARE	@StoredFkCount int = (SELECT COUNT(*) FROM [adm].[ForeignKeyDefinitions]),
			@DbFkCount int = (SELECT COUNT(*) FROM sys.foreign_keys);
	PRINT '----- Manage Foreign Keys -----';
	PRINT 'Pre-Execution - Number of stored foreign keys (in [adm].[ForeignKeyDefinitions]): ' + CAST(@StoredFkCount as nvarchar(20));
	PRINT 'Pre-Execution - Number of foreign keys in this database: ' + ISNULL(CAST(@DbFkCount as nvarchar(20)), '<null>');
	PRINT '';


	-- Get a list of FKs currently active in the DB.
	DECLARE @FkInfo table (	[Fk] nvarchar(1000), [ParentSchema] nvarchar(20), [ParentTable] nvarchar(100), [ParentColumn] nvarchar(100), [ReferencedSchema] nvarchar(20), 
							[ReferencedTable] nvarchar(100), [ReferencedColumn] nvarchar(100), [ExistsInDb] bit, [IsStored] bit, [Description] nvarchar(1000),
							[CreateFkSql] nvarchar(1000), [DropFkSql] nvarchar(1000), [InsertIntoStoredSql] nvarchar(1000), [DeleteFromStoredSql] nvarchar(1000)	);
	INSERT INTO @FkInfo
		SELECT	Fk = SFK.[name],
				ParentSchema = SCHEMA_NAME(SOP.[schema_id]),
				ParentTable = OBJECT_NAME(SFK.[parent_object_id]),
				ParentColumn = COL_NAME(SFKC.[parent_object_id], SFKC.[parent_column_id]),
				ReferencedSchema = SCHEMA_NAME(SOR.[schema_id]),
				ReferencedTable = OBJECT_NAME(SFK.[referenced_object_id]),
				ReferencedColumn = COL_NAME(SFKC.[referenced_object_id], SFKC.[referenced_column_id]),
				ExistsInDb = 1,
				IsStored = 0,
				[Description] = NULL, 
				CreateFkSql = NULL, 
				DropFkSql = NULL, 
				InsertIntoStoredSql = NULL, 
				DeleteFromStoredSql = NULL
		FROM	sys.foreign_keys SFK
				INNER JOIN sys.foreign_key_columns SFKC
				 ON SFK.[object_id] = SFKC.[constraint_object_id]
				INNER JOIN sys.objects SOP
				 ON SFKC.[parent_object_id] = SOP.[object_id]
				INNER JOIN sys.objects SOR
				 ON SFKC.[referenced_object_id] = SOR.[object_id];


	-- Mark the Stored ones.
	UPDATE	DBFK
	SET		IsStored = 1
	FROM	@FkInfo DBFK
	WHERE	EXISTS (	SELECT	* 
						FROM	[adm].[ForeignKeyDefinitions]  FKD
						WHERE	FKD.ParentSchema = DBFK.ParentSchema
							AND	FKD.ParentTable = DBFK.ParentTable
							AND	FKD.ParentColumn = DBFK.ParentColumn
							AND	FKD.ReferencedSchema = DBFK.ReferencedSchema
							AND	FKD.ReferencedTable = DBFK.ReferencedTable
							AND	FKD.ReferencedColumn = DBFK.ReferencedColumn );


	-- Add any Stored that are not in the DB.
	INSERT INTO @FkInfo
		SELECT	Fk = LEFT('FK_' + [ParentSchema] + [ParentTable] + '_' + [ParentColumn] + '_' + [ReferencedSchema] + [ReferencedTable] + '_' + [ReferencedColumn], 128),
				ParentSchema, ParentTable, ParentColumn, ReferencedSchema, 
				ReferencedTable, ReferencedColumn, ExistsInDb=0, IsStored=1, [Description]=NULL,
				CreateFkSql=NULL, DropFkSql=NULL, InsertToStoreSql=NULL, DeleteFromStoreSql=NULL
		FROM	[adm].[ForeignKeyDefinitions] FKD
		WHERE	NOT EXISTS (	SELECT	*
								FROM	@FkInfo DBFK
								WHERE	DBFK.ParentSchema = FKD.ParentSchema
								  AND	DBFK.ParentTable = FKD.ParentTable
								  AND	DBFK.ParentColumn = FKD.ParentColumn
								  AND	DBFK.ReferencedSchema = FKD.ReferencedSchema
								  AND	DBFK.ReferencedTable = FKD.ReferencedTable
								  AND	DBFK.ReferencedColumn = FKD.ReferencedColumn );


	-- Populte all possible Sql.
	UPDATE	DBFK
	SET		[Description] = 'ParentSchema=''' + [ParentSchema] + '''; ParentTable=''' + [ParentTable] + '''; ParentColumn=''' + [ParentColumn] + '''; ReferencedSchema=''' + [ReferencedSchema] + '''; ReferencedTable=''' + [ReferencedTable] + '''; ReferencedColumn=''' + [ReferencedColumn] + '''',
			[CreateFkSql] = 'ALTER TABLE [' + [ParentSchema] + '].[' + [ParentTable] + '] WITH CHECK ADD CONSTRAINT [' + [Fk] + '] FOREIGN KEY([' + [ParentColumn] + ']) REFERENCES [' + [ReferencedSchema] + '].[' + [ReferencedTable] + '] ([' + [ReferencedColumn] + ']); ALTER TABLE [' + [ParentSchema] + '].[' + [ParentTable] + '] CHECK CONSTRAINT [' + [Fk] + '];',
			[DropFkSql] = 'ALTER TABLE [' + [ParentSchema] + '].[' + [ParentTable] + '] DROP CONSTRAINT [' + [Fk] + '];',
			[InsertIntoStoredSql] = 'INSERT INTO [adm].[ForeignKeyDefinitions] VALUES (''' + CONVERT(nvarchar, @Now, 121) + ''', ''' + [ParentSchema] + ''', ''' + [ParentTable] + ''', ''' + [ParentColumn] + ''', ''' + [ReferencedSchema] + ''', ''' + [ReferencedTable] + ''', ''' + [ReferencedColumn] + ''');',
			[DeleteFromStoredSql] = 'DELETE FROM [adm].[ForeignKeyDefinitions] WHERE [ParentSchema] = ''' + [ParentSchema] + ''' AND [ParentTable] = ''' + [ParentTable] + ''' AND [ParentColumn] = ''' + [ParentColumn] + ''' AND [ReferencedSchema] = ''' + [ReferencedSchema] + ''' AND [ReferencedTable] = ''' + [ReferencedTable] + ''' AND [ReferencedColumn] = ''' + [ReferencedColumn] + ''';'
	FROM	@FkInfo DBFK;


	-- Spit out info (if so directed).
	IF( (@RemoveAllForeignKeys=0) AND (@ReAddAllForeignkeys=0) AND (@Directive='')) BEGIN
		PRINT ' To gather info about ForeignKeys and stored ForeignKeys (in the [adm].[ForeignKeyDefinitions] table), run this stored';
		PRINT ' procedure with the @Directive argument set to ''JustShowFkInfoTable''.'
		GOTO EXIT_STORED_PROCEDURE;
	END
	IF(@Directive = 'JustShowFkInfoTable') BEGIN
		SELECT * FROM @FkInfo;
		GOTO EXIT_STORED_PROCEDURE;
	END


	-- Some info to make logic below more easy to read.
	DECLARE	@DbHasFKs bit = CASE WHEN ((SELECT COUNT(*) FROM @FkInfo WHERE [ExistsInDb]=1) > 0) THEN 1 ELSE 0 END,
			@DbHasStoredFKs bit = CASE WHEN ((SELECT COUNT(*) FROM @FkInfo WHERE [IsStored]=1) > 0) THEN 1 ELSE 0 END,
			@DbHasUnStoredFks bit = CASE WHEN ((SELECT COUNT(*) FROM @FkInfo WHERE [ExistsInDb]=1 AND [IsStored]=0) > 0) THEN 1 ELSE 0 END;


	-- Switchboard for removing all ForeignKeys.
	DECLARE @DoRemoveAll bit = 0, @StoreDbsNewFKs bit = 0, @DoReAddAll bit = 0;
	IF(@RemoveAllForeignKeys = 1) BEGIN

		IF( @DbHasFKs = 0 ) BEGIN
			PRINT ' * NOTE:	Nothing to do.  There are currenlty no ForeignKeys in this database.';
			GOTO EXIT_STORED_PROCEDURE;
		END
		IF( (@DbHasUnStoredFks = 1) AND (@Directive NOT IN ('StoreDbsNewForeignKeys', 'LooseDbsNewForeignKeys')) ) BEGIN
			PRINT ' * NOTE:	Can''t remove all ForeignKeys from this database because ForeignKeys exist in the database that have not been'
			PRINT '			saved to the [adm].[ForeignKeyDefinitions] table.  Options:  '
			PRINT '				1)	Set the @Directives argument to ''StoreDbsNewForeignKeys'' to store all non-stored ForeignKeys.';
			PRINT '				2)	Set the @Directives argument to ''LooseDbsNewForeignKeys'' to ignore all non-stored ForeignKeys (they will be lost).';
			GOTO EXIT_STORED_PROCEDURE;
		END ELSE IF( (@DbHasUnStoredFks = 1) AND (@Directive IN ('StoreDbsNewForeignKeys', 'LooseDbsNewForeignKeys')) ) BEGIN
			IF(@Directive = 'StoreDbsNewForeignKeys') BEGIN
				PRINT ' * NOTE: Saving all new ForeignKeys to the [adm].[ForeignKeyDefinitions] table (since @Directive is set to'
				PRINT '		''StoreDbsNewForeignKeys'').  Then removing all ForeignKeys from this database.'
				SET @StoreDbsNewFKs = 1;
			END ELSE BEGIN
				PRINT ' * NOTE: Ignoring all new ForeignKeys (since @Directive is set to ''LooseDbsNewForeignKeys'') and removing';
				PRINT '			all ForeignKeys from this database.  As a result, all ForeignKeys not in the [adm].[ForeignKeyDefinitions]';
				PRINT '			table will be lost.';
			END
			SET @DoRemoveAll = 1;
		END ELSE IF(@DbHasUnStoredFks = 0) BEGIN
			-- Most typical (the standard) situation when removing ForeignKeys.
			PRINT ' Removing all ForeignKeys from this database.  Since all ForeignKeys in this database have been stored in the'
			PRINT ' [adm].[ForeignKeyDefinitions] table, they can be re-added easily by calling this stored procedure with';
			PRINT ' @ReAddAllForeignkeys set to ''1''.';
			SET @DoRemoveAll = 1;
		END ELSE BEGIN
			PRINT ' ERROR:  Unabe to remove all ForeignKeys.  Unaticipated ForeignKey situation.';
			GOTO EXIT_STORED_PROCEDURE;
		END

	END


	-- Switchboard for re-adding all ForeignKeys.
	IF(@ReAddAllForeignkeys = 1) BEGIN

		IF( @DbHasStoredFKs = 0 ) BEGIN
			PRINT ' Nothing to do.  There is no stored ForeignKeys information in the [adm].[ForeignKeyDefinitions] table.';
			GOTO EXIT_STORED_PROCEDURE;
		END
		IF( (@DbHasFKs = 1) AND (@Directive != 'ReAddForeignKeysAnyway') ) BEGIN
			IF(@StoredFkCount != @DbFkCount) BEGIN
				PRINT ' * NOTE:	ForeignKeys currently exist in this database.  It is unusual to re-add ForeignKeys from information stored in';
				PRINT '			the [adm].[ForeignKeyDefinitions] table to a database with existing ForeignKeys.  If you wish to proceed, Set';
				PRINT '			the @Directives argument to ''ReAddForeignKeysAnyway''.';
			END ELSE BEGIN
				PRINT ' Nothing to do.  ForeignKeys exist in the database for all ' + CONVERT(nvarchar(20), @DbFkCount) + ' ForiegnKeys stored in'
				PRINT ' the [adm].[ForeignKeyDefinitions] table.';
			END
			GOTO EXIT_STORED_PROCEDURE;
		END ELSE  IF( (@DbHasFKs = 1) AND (@Directive = 'ReAddForeignKeysAnyway') ) BEGIN
			PRINT ' * NOTE:	A ForeignKey will be added for each row in the [adm].[ForeignKeyDefinitions] table.  ForeignKeys currenlty';
			PRINT '			exist in the database, so the [adm].[ForeignKeyDefinitions] table is out of synch with reality.';
			SET @DoReAddAll = 1;
		END ELSE IF( (@DbHasFKs = 0) ) BEGIN
			-- Most typical (the standard) situation when removing ForeignKeys.
			PRINT ' Re-creating all ForeignKeys in this database from information stored in the [adm].[ForeignKeyDefinitions] table.';
			PRINT ' Note: To prevent a stored ForeignKey from being re-created, delete its row from the [adm].[ForeignKeyDefinitions] table.';
			SET @DoReAddAll = 1;
		END ELSE BEGIN
			PRINT ' ERROR:  Unabe to re-add all ForeignKeys.  Unaticipated ForeignKey situation.';
			GOTO EXIT_STORED_PROCEDURE;
		END

	END


	-- Collect Sql to execute.
	DECLARE @Work table ([Type] nvarchar(20), [Fk] nvarchar(255), [Sql] nvarchar(1000));
	IF(@StoreDbsNewFKs = 1) BEGIN
		INSERT INTO @Work 
			SELECT	'InsertIntoStoredSql', [Fk], [InsertIntoStoredSql]
			FROM	@FkInfo
			WHERE	[ExistsInDb] = 1 AND [IsStored] = 0;
	END
	IF(@DoRemoveAll = 1) BEGIN
		INSERT INTO @Work 
			SELECT	'DropFkSql', [Fk], [DropFkSql]
			FROM	@FkInfo
			WHERE	[ExistsInDb] = 1;
	END
	IF(@DoReAddAll = 1) BEGIN
		INSERT INTO @Work 
			SELECT	'CreateFkSql', [Fk], [CreateFkSql]
			FROM	@FkInfo
			WHERE	[ExistsInDb] = 0 AND [IsStored] = 1;
	END


	-- Do synching work.
	PRINT '';
	DECLARE @DoneMsg nvarchar(500), @Type nvarchar(20), @Fk nvarchar(255), @Sql nvarchar(2000);
	DECLARE FkInfo_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[Type], [Fk], [Sql]
			FROM	@Work
	OPEN FkInfo_CURSOR
	FETCH NEXT FROM FkInfo_CURSOR INTO @Type, @Fk, @Sql
	WHILE (@@fetch_status <> -1) BEGIN
		BEGIN TRY
			EXEC(@Sql);
			SET @DoneMsg =  CASE (@Type)
								WHEN 'InsertIntoStoredSql' THEN 'Stored foreign Key info in [adm].[ForeignKeyDefinitions]'
								WHEN 'DropFkSql' THEN 'Dropped foreign Key'
								WHEN 'CreateFkSql' THEN 'Created foreign Key'
								ELSE '{unknown work type}'
							END;
			PRINT '   ' + @DoneMsg + ':  ' + @Fk;
		END TRY
		BEGIN CATCH
			DECLARE @ErrMsg nvarchar(500) = ERROR_MESSAGE();
			RAISERROR(@ErrMsg, 16, -1);
			PRINT '   This ''' + @Type + ''' task failed due to above error: ' + @Sql
			IF(@Type = 'InsertIntoStoredSql') BEGIN 
				PRINT '   ***** Process halted since ForeignKey data could be lost if additional tasks are executed *****'
				GOTO EXIT_STORED_PROCEDURE;
			END
		END CATCH
		FETCH NEXT FROM FkInfo_CURSOR INTO @Type, @Fk, @Sql
	END
	CLOSE FkInfo_CURSOR DEALLOCATE FkInfo_CURSOR;

	-- Post-Execution Statistics.
	SET @StoredFkCount = (SELECT COUNT(*) FROM [adm].[ForeignKeyDefinitions]);
	SET @DbFkCount = (SELECT COUNT(*) FROM sys.foreign_keys);
	PRINT '';
	PRINT 'Post-Execution - Number of stored foreign keys (in [adm].[ForeignKeyDefinitions]): ' + CAST(@StoredFkCount as nvarchar(20));
	PRINT 'Post-Execution - Number of foreign keys in this database: ' + ISNULL(CAST(@DbFkCount as nvarchar(20)), '<null>');
	PRINT '';

	EXIT_STORED_PROCEDURE:
END



-- *************************************************************************
-- *****  *****
-- *************************************************************************

-- ============================================= -- Author: Markus Schippel -- Create date: 2024-01-18 -- Description: Manages foreign keys for this database by storing ones that need to be kept around -- in the [adm].[ForeignKeyDefinitions] table. -- ============================================= -- EXEC [adm].[usp_ManageForeignKeys]; -- Analyze -- EXEC [adm].[usp_ManageForeignKeys] @Directive='JustShowFkInfoTable'; -- Analyze by returning table with FK info and sql to do manual tweaking ------------------------------------------------ -- --EXEC [adm].[usp_ManageForeignKeys] @RemoveAllForeignKeys=1; -- Remove all FKs -- EXEC [adm].[usp_ManageForeignKeys] @RemoveAllForeignKeys=1, @Directive='StoreDbsNewForeignKeys'; -- Remove all FKs (but store new ones fist!) -- --EXEC [adm].[usp_ManageForeignKeys] @RemoveAllForeignKeys=1, @Directive='LooseDbsNewForeignKeys'; -- Remove all FKs (loose new ones) **CAREFULL** ------------------------------------------------ -- EXEC [adm].[usp_ManageForeignKeys] @ReAddAllForeignkeys=1; -- Re-add stored FKs -- EXEC [adm].[usp_ManageForeignKeys] @ReAddAllForeignkeys=1, @Directive='ReAddForeignKeysAnyway'; -- Re-add stored FKs (even thought there are new ones) CREATE PROCEDURE [adm].[usp_ManageForeignKeys]
@RemoveAllForeignKeys bit = 0, @ReAddAllForeignkeys bit = 0, @Directive nvarchar(500) = ''
AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY

	-- Grab counts of Inserted and Deleted.
	DECLARE	@CountDel int = (SELECT COUNT(*) FROM OPENJSON(@AuditJson, '$.Deleted')),
			@CountIns int = (SELECT COUNT(*) FROM OPENJSON(@AuditJson, '$.Inserted'));
	IF (@CountDel + @CountIns = 0) GOTO EXIT_HANDLER;

	-- Pull out Id and LastUpdateBy.
	DECLARE	@UniqueId bigint = (CASE WHEN (@CountIns > 0) THEN (SELECT JSON_VALUE(@AuditJson, '$.Inserted[0].Id')) ELSE (SELECT JSON_VALUE(@AuditJson, '$.Deleted[0].Id')) END),
			@UpdateBy nvarchar(255) = (CASE WHEN (@CountIns > 0) THEN (SELECT JSON_VALUE(@AuditJson, '$.Inserted[0].LastUpdateBy')) ELSE '{unknown}' END);

	-- Set AuditType.
	DECLARE @AuditType char(1) = CASE WHEN (@CountDel<1) THEN 'I' WHEN (@CountIns<1) THEN 'D' ELSE 'U' END, 
			@Count int = CASE WHEN (@CountIns>0) THEN @CountIns ELSE @CountDel END;

	-- Switch AuditType from 'Update' to 'Pseudo-delete' if DateDeactivated was set on Inserted.
	IF (@AuditType = 'U' AND (SELECT JSON_VALUE(@AuditJson, '$.Deleted[0].DateDeactivated')) IS NULL AND (SELECT JSON_VALUE(@AuditJson, '$.Inserted[0].DateDeactivated')) IS NOT NULL) SET @AuditType = 'P';

	-- Create TableAudit record.
	INSERT INTO [adm].[TableAudit]
		([DbUser], [UpdateBy], [AuditType], [TableName], [UniqueId], [RowCount], [AuditJson])
		VALUES
		(SYSTEM_USER, COALESCE(@UpdateBy, '{unknown}'), @AuditType, @TableName, @UniqueId, @Count , @AuditJson);

EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- ============================================= -- Author: Markus Schippel -- Create date: 2024-03-06 -- Description: Process a table's data change (insert/update/delete) audit. -- ============================================= CREATE PROCEDURE [adm].[usp_ManageTableAudit]
@TableName nvarchar(100), @AuditJson nvarchar(max)
AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY

	-- SELECT * FROM [sys].[indexes] WHERE [name] LIKE '%IX%'
	DECLARE @Sql nvarchar(500), @IndexName nvarchar(255), @IndexCreateName nvarchar(255), @ExistCount int = 0,
			@Sch nvarchar(20), @Tbl nvarchar(100), @Col nvarchar(100);

	-- Create an index on all DateDeactivated/DateDeleted/IsDeleted fields.
	-- This is vital since all selects should check this (pseudo-delete).
	DECLARE JNTBL_INDEX_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[TableSchema], [TableName], [ColumnName]
			FROM	[adm].[vDbColumnInfo]
			WHERE	(	([FkTableName] IS NOT NULL)
						OR (LEFT([TableName], 2) = 'jn' AND [IsIdentity] = 0 AND [DataType] IN ('int', 'smallint')) --(should have been covered in FK check)
						OR ([ColumnName] IN ('DateDeactivated', 'DateDeleted', 'IsDeleted', 'IsActive', 'IsInactive') )
					)
			 -- AND	(@DictatedTableSchema IS NULL OR @DictatedTableSchema = [TableSchema])
			ORDER BY [TableSchema], [TableName], [ColumnId];
	OPEN JNTBL_INDEX_CURSOR
	FETCH NEXT FROM JNTBL_INDEX_CURSOR INTO @Sch, @Tbl, @Col
	WHILE (@@fetch_status <> -1)
	BEGIN

		--PRINT CONCAT('[', @Sch, '].[', @Tbl, '].[', @Col, ']');
		SET	@IndexName = CONCAT('IX_', @Sch, @Tbl, '_' , @Col)
		SET	@IndexCreateName = CONCAT(@IndexName, ' ON [', @Sch, '].[', @Tbl, ']')
		IF (NOT EXISTS (SELECT [object_id] FROM [sys].[indexes] WHERE [name] = @IndexName)) BEGIN
			PRINT CONCAT('Index missing (will be create):  ''' , @IndexName, '''')
		END ELSE BEGIN
			SET @ExistCount = @ExistCount + 1;
		END

		SET @Sql = CONCAT('DROP INDEX IF EXISTS ', @IndexCreateName, ';');
		EXEC sp_ExecuteSql @Sql;

		SET @Sql = CONCAT('CREATE NONCLUSTERED INDEX ', @IndexCreateName, '([', @Col , '] ASC);');
		EXEC sp_ExecuteSql @Sql;

		FETCH NEXT FROM JNTBL_INDEX_CURSOR INTO @Sch, @Tbl, @Col
	END
	CLOSE JNTBL_INDEX_CURSOR; DEALLOCATE JNTBL_INDEX_CURSOR
	PRINT CONCAT('*** ', @ExistCount, ' Indexes previously existed and were recreated. ***')

EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY

	-- Create TableAudit and LookupCheck triggers.
	-- Note:  The called stored procedures delete and then create triggers by naming convention.
	DECLARE	@TableSchema nvarchar(50), @TableName nvarchar(255), @IsLookupTable bit, @Line nvarchar(1000);
	DECLARE TRIGGER_CREATE_CURSOR CURSOR READ_ONLY
	FOR		SELECT	[TableSchema], [TableName], [IsLookupTable] = (CASE WHEN (LEFT([TableName], 2) = 'lk') THEN 1 ELSE 0 END)
			FROM	[adm].[vDbColumnInfo]
			WHERE	[ColumnId] = 1
			  AND	[TableSchema] != 'adm'
			  AND	LEFT([TableName], 2) != 'jn'
			ORDER BY [TableSchema], [TableName], [ColumnId];
	OPEN TRIGGER_CREATE_CURSOR
	FETCH NEXT FROM TRIGGER_CREATE_CURSOR INTO @TableSchema, @TableName, @IsLookupTable
	WHILE (@@fetch_status <> -1)
	BEGIN

		--PRINT CONCAT('[', @TableSchema, '].[', @TableName, '] (', @IsLookupTable, ')');
		EXEC [adm].[usp_CreateTableAuditTrigger] @TableName, @TableSchema;

		--IF(@IsLookupTable = 1) BEGIN
		--	EXEC [adm].[usp_CreateLookupCheckTrigger] @TableName, @TableSchema;
		--END

		FETCH NEXT FROM TRIGGER_CREATE_CURSOR INTO @TableSchema, @TableName, @IsLookupTable
	END
	CLOSE TRIGGER_CREATE_CURSOR; DEALLOCATE TRIGGER_CREATE_CURSOR

EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY
	DECLARE @ErrMsg nvarchar(255);

	DECLARE @SPName nvarchar(1000) = '['+OBJECT_SCHEMA_NAME(@@PROCID)+'].['+OBJECT_NAME(@@PROCID)+']';
	PRINT CONCAT('This Name: ''', @SPName, '''');

	PRINT CONCAT('@SupplyId: ''', @SupplyId, '''');
	PRINT CONCAT('@LocationId: ''', @LocationId, '''');
	PRINT CONCAT('@AdjustmentQuantity: ''', @AdjustmentQuantity, '''');
	PRINT CONCAT('@Reason: ''', @Reason, '''');
	PRINT CONCAT('@UpdateBy: ''', @UpdateBy, '''');
	PRINT ' --'

	-- Validate Supply.
	IF(NOT EXISTS((SELECT [Id] FROM [dbo].[Supply] WHERE [Id] = @SupplyId AND [DateDeactivated] IS NULL))) BEGIN
		SET @ErrMsg = 'Passed in @SupplyId does not point to an active Supply.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Validate Location.
	IF(NOT EXISTS((SELECT [Id] FROM [dbo].[Location] WHERE [Id] = @LocationId AND [DateDeactivated] IS NULL))) BEGIN
		SET @ErrMsg = 'Passed in @LocationId does not point to an active Location.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Validate AdjustmentQuantity.
	IF(@AdjustmentQuantity IS NULL OR @AdjustmentQuantity = 0) BEGIN
		SET @ErrMsg = 'Passed int @AdjustmentQuantity must be a positive or negative number.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Value for LastUpdateBy.
	IF(@UpdateBy IS NULL) SET @UpdateBy = CONCAT('{', @SPName, '}');

	-- Update quantity on Inventory record for passed in Supply.
	DECLARE @InventoryId int = (SELECT TOP 1 [Id] FROM [dbo].[Inventory] WHERE [SupplyId] = @SupplyId);
	PRINT CONCAT('@InventoryId: ''', @InventoryId, '''');
	IF (@InventoryId IS NULL) BEGIN
		SET @ErrMsg = 'No Inventory record found for passed in @SupplyId.';
		RAISERROR (@ErrMsg, 16 , -1);
	END
	UPDATE	[dbo].[Inventory]
	SET		[Quantity] = [Quantity] + @AdjustmentQuantity, 
			[LastUpdateBy] = @UpdateBy
	WHERE	[Id] = @InventoryId;

	-- Update quantity on InventoryLocation record for passed in Supply.
	DECLARE @InventoryLocationId int = (	SELECT TOP 1 [Id] 
											FROM	[dbo].[InventoryLocation] 
											WHERE	[SupplyId] = @SupplyId AND [LocationId] = @LocationId);
	PRINT CONCAT('@InventoryLocationId: ''', @InventoryLocationId, '''');
	IF (@InventoryLocationId IS NULL) BEGIN
		SET @ErrMsg = 'No InventoryLocation record found for passed in @SupplyId and @LocationId.';
		RAISERROR (@ErrMsg, 16 , -1);
	END ELSE BEGIN
		UPDATE	[dbo].[InventoryLocation]
		SET		[Quantity] = [Quantity] + @AdjustmentQuantity,
				[LastUpdateBy] = @UpdateBy
		WHERE	[Id] = @InventoryLocationId;
	END

	-- Add an InventoryTransaction to record this transaction.
	DECLARE @InvTranTypeId smallint = (SELECT [Id] FROM [dbo].[lkInventoryTransactionType] WHERE [Code] = 'ADJ');
	INSERT INTO [dbo].[InventoryTransaction] ([InventoryTransactionTypeId], [SupplyId], [Quantity], [Description], [LastUpdateBy])
		VALUES	(@InvTranTypeId, @SupplyId, @AdjustmentQuantity, @Reason, @UpdateBy);


EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- =============================================
-- Author:		Markus Schippel
-- Create date: 2024-10-29
-- Description:	Record adjustment to inventory.
-- =============================================
-- EXEC [dbo].[usp_AdjustInventory] 1, 1, -15, 'dropped box of honey', 'Jan';
CREATE PROCEDURE [dbo].[usp_AdjustInventory]

	@SupplyId nvarchar(25),
	@LocationId nvarchar(25),
	@AdjustmentQuantity int,
	@Reason nvarchar(500) = NULL,
	@UpdateBy nvarchar(255) = NULL

AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY
	DECLARE @ErrMsg nvarchar(255);

	DECLARE @SPName nvarchar(1000) = '['+OBJECT_SCHEMA_NAME(@@PROCID)+'].['+OBJECT_NAME(@@PROCID)+']';
	PRINT CONCAT('This Name: ''', @SPName, '''');

	PRINT CONCAT('@EventDate: ''', @EventDate, '''');
	PRINT CONCAT('@EventDefinitionIdOrCode: ''', @EventDefinitionIdOrCode, '''');
	PRINT CONCAT('@VendorPersonId: ''', @VendorPersonId, '''');
	PRINT CONCAT('@UpdateBy: ''', @UpdateBy, '''');
	PRINT CONCAT('@BothSpace: ''', @BothSpace, '''');
	PRINT CONCAT('@PettyCash: ''', @PettyCash, '''');

	-- Validate @EventDate.
	IF(@EventDate IS NULL) SET @EventDate = DATEADD(day, 1, GETDATE());

	-- Set/validate EventDefinition.
	DECLARE @EventDefinitionId int = (SELECT [Id] FROM [dbo].[EventDefinition] WHERE [Id] = @EventDefinitionIdOrCode OR [Code] = @EventDefinitionIdOrCode);
	IF(@EventDefinitionId IS NULL) BEGIN
		SET @ErrMsg = 'Passed int @EventDefinitionIdOrCode does not point to an existing EventDefinition.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Set/validate Vendor (Person).
	DECLARE @PersonId int = (SELECT [Id] FROM [dbo].[Person] WHERE [Id] = @VendorPersonId);
	IF(@VendorPersonId IS NULL) BEGIN
		SET @ErrMsg = 'Passed int @VendorPersonId does not point to an existing Person.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	IF(@UpdateBy IS NULL) SET @UpdateBy = CONCAT('{', @SPName, '}');

	-- Create Event.
	INSERT INTO [dbo].[Event] ([EventDefinitionId], [EventDate], [VendorPersonId], [BoothSpace], [PettyCash], [LastUpdateBy])
		SELECT	[Id], 
				(CONCAT(@EventDate, ' ', CONVERT (time, [ScheduledStartTime]), ' ', DATENAME(tzoffset, [ScheduledStartTime]))),
				@PersonId,
				COALESCE(@BothSpace, [DefaultBoothSpace]),
				COALESCE(@PettyCash, [DefaultPettyCash]),
				@UpdateBy
		FROM	[dbo].[EventDefinition]
		WHERE	[Id] = @EventDefinitionId;
	DECLARE @EventId int = SCOPE_IDENTITY();

	-- Create EventProducts.
	INSERT INTO [dbo].[EventProduct] ([EventId], [ProductId], [TargetQuantity], [StartQuantity], [DiscountAmount], [LastUpdateBy])
		SELECT	@EventId, [ProductId], [Quantity], [Quantity], 0.00, @UpdateBy
		FROM	[dbo].[EventDefinitionProduct]
		WHERE	[EventDefinitionId] = @EventDefinitionId;


EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- =============================================
-- Author:		Markus Schippel
-- Create date: 2024-10-25
-- Description:	Create an Event based on an EventTemplate.
-- =============================================
-- EXEC [dbo].[usp_CreateEvent] '2024-10-17', 4, 3, 'DUser';
CREATE PROCEDURE [dbo].[usp_CreateEvent]

	@EventDate date,
	@EventDefinitionIdOrCode nvarchar(25),
	@VendorPersonId int,
	@UpdateBy nvarchar(255) = NULL,
	@BothSpace nvarchar(255) = NULL,
	@PettyCash decimal(18,2) = NULL
	
AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY
	DECLARE @ErrMsg nvarchar(255);

	DECLARE @SPName nvarchar(1000) = '['+OBJECT_SCHEMA_NAME(@@PROCID)+'].['+OBJECT_NAME(@@PROCID)+']';
	PRINT CONCAT('This Name: ''', @SPName, '''');

	PRINT CONCAT('@SupplyId: ''', @SupplyId, '''');
	PRINT CONCAT('@OldLocationId: ''', @OldLocationId, '''');
	PRINT CONCAT('@NewLocationId: ''', @NewLocationId, '''');
	PRINT CONCAT('@Quantity: ''', @Quantity, '''');
	PRINT CONCAT('@Reason: ''', @Reason, '''');
	PRINT CONCAT('@UpdateBy: ''', @UpdateBy, '''');
	PRINT ' --'

	-- Validate Supply.
	IF(NOT EXISTS((SELECT [Id] FROM [dbo].[Supply] WHERE [Id] = @SupplyId AND [DateDeactivated] IS NULL))) BEGIN
		SET @ErrMsg = 'Passed in @SupplyId does not point to an active Supply.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Validate "Old" Location.
	IF(NOT EXISTS((SELECT [Id] FROM [dbo].[Location] WHERE [Id] = @OldLocationId AND [DateDeactivated] IS NULL))) BEGIN
		SET @ErrMsg = 'Passed in @OldLocationId does not point to an active Location.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Validate "New" Location.
	IF(NOT EXISTS((SELECT [Id] FROM [dbo].[Location] WHERE [Id] = @NewLocationId AND [DateDeactivated] IS NULL))) BEGIN
		SET @ErrMsg = 'Passed in @NewLocationId does not point to an active Location.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Validate Quantity.
	IF(@Quantity IS NULL OR @Quantity < 1) BEGIN
		SET @ErrMsg = 'Passed int @Quantity is missing or less than 1.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Value for LastUpdateBy.
	IF(@UpdateBy IS NULL) SET @UpdateBy = CONCAT('{', @SPName, '}');


	-- Update quantity on "Old" InventoryLocation record for passed in Supply.
	DECLARE @OldInventoryLocationId int = (	SELECT TOP 1 [Id] 
											FROM	[dbo].[InventoryLocation] 
											WHERE	[SupplyId] = @SupplyId AND [LocationId] = @OldLocationId);
	PRINT CONCAT('@OldInventoryLocationId: ''', @OldInventoryLocationId, '''');
	IF (@OldInventoryLocationId IS NULL) BEGIN
		SET @ErrMsg = 'No InventoryLocation record found for passed in @SupplyId and @OldInventoryLocationId.';
		RAISERROR (@ErrMsg, 16 , -1);
	END ELSE BEGIN
		UPDATE	[dbo].[InventoryLocation]
		SET		[Quantity] = [Quantity] - @Quantity,
				[LastUpdateBy] = @UpdateBy
		WHERE	[Id] = @OldInventoryLocationId;
	END


	-- Update quantity on "New" InventoryLocation record for passed in Supply.
	DECLARE @NewInventoryLocationId int = (	SELECT TOP 1 [Id] 
											FROM	[dbo].[InventoryLocation] 
											WHERE	[SupplyId] = @SupplyId AND [LocationId] = @NewLocationId);
	PRINT CONCAT('@NewInventoryLocationId: ''', @NewInventoryLocationId, '''');
	IF (@NewInventoryLocationId IS NULL) BEGIN
		INSERT INTO [dbo].[InventoryLocation] ([SupplyId], [Quantity], [LocationId], [LastUpdateBy])
			VALUES	(@SupplyId, @Quantity, @NewLocationId, @UpdateBy);
	END ELSE BEGIN
		UPDATE	[dbo].[InventoryLocation]
		SET		[Quantity] = [Quantity] + @Quantity,
				[LastUpdateBy] = @UpdateBy
		WHERE	[Id] = @NewInventoryLocationId;
	END

	-- Add an InventoryTransaction to record this transaction.
	DECLARE @InvTranTypeId smallint = (SELECT [Id] FROM [dbo].[lkInventoryTransactionType] WHERE [Code] = 'MOVE');
	INSERT INTO [dbo].[InventoryTransaction] ([InventoryTransactionTypeId], [SupplyId], [Quantity], [OldLocationId], [NewLocationId], [Description], [LastUpdateBy])
		VALUES	(@InvTranTypeId, @SupplyId, @Quantity, @OldLocationId, @NewLocationId, @Reason, @UpdateBy);


EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

-- =============================================
-- Author:		Markus Schippel
-- Create date: 2024-10-29
-- Description:	Record adjustment to inventory.
-- =============================================
-- EXEC [dbo].[usp_MoveInventory] 1, 2, 1, 11, NULL, 'James';
CREATE PROCEDURE [dbo].[usp_MoveInventory]

	@SupplyId nvarchar(25),
	@OldLocationId nvarchar(25),
	@NewLocationId nvarchar(25),
	@Quantity int,
	@Reason nvarchar(500) = NULL,
	@UpdateBy nvarchar(255) = NULL

AS

BEGIN
SET NOCOUNT ON;
BEGIN TRANSACTION;
BEGIN TRY
	DECLARE @ErrMsg nvarchar(255);

	DECLARE @SPName nvarchar(1000) = '['+OBJECT_SCHEMA_NAME(@@PROCID)+'].['+OBJECT_NAME(@@PROCID)+']';
	PRINT CONCAT('This Name: ''', @SPName, '''');

	PRINT CONCAT('@SupplyId: ''', @SupplyId, '''');
	PRINT CONCAT('@LocationId: ''', @LocationId, '''');
	PRINT CONCAT('@Quantity: ''', @Quantity, '''');
	PRINT CONCAT('@TotalCost: ''', @TotalCost, '''');
	PRINT CONCAT('@SupplyCompanyId: ''', @SupplyCompanyId, '''');
	PRINT CONCAT('@UpdateBy: ''', @UpdateBy, '''');
	PRINT ' --'

	DECLARE @CostPer decimal(18,2) = @TotalCost / CAST(@Quantity as decimal(18,2));

	PRINT CONCAT('@CostPer: ''', @CostPer, '''');

	-- Validate Supply.
	IF(NOT EXISTS((SELECT [Id] FROM [dbo].[Supply] WHERE [Id] = @SupplyId AND [DateDeactivated] IS NULL))) BEGIN
		SET @ErrMsg = 'Passed in @SupplyId does not point to an active Supply.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Validate Location.
	IF(NOT EXISTS((SELECT [Id] FROM [dbo].[Location] WHERE [Id] = @LocationId AND [DateDeactivated] IS NULL))) BEGIN
		SET @ErrMsg = 'Passed in @LocationId does not point to an active Location.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Validate SupplyCompany.
	IF((@SupplyCompanyId IS NOT NULL) AND (NOT EXISTS((SELECT [Id] FROM [dbo].[Company] WHERE [Id] = @SupplyCompanyId AND [DateDeactivated] IS NULL)))) BEGIN
		SET @ErrMsg = 'Passed in @SupplyCompanyId does not point to an active Company.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Validate Quantity.
	IF(@Quantity IS NULL OR @Quantity < 1) BEGIN
		SET @ErrMsg = 'Passed int @Quantity is missing or less than 1.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Validate TotalCost.
	IF(@TotalCost IS NULL OR @TotalCost < 0.01) BEGIN
		SET @ErrMsg = 'Passed int @TotalCost is missing or less than 0.01.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	-- Value for LastUpdateBy.
	IF(@UpdateBy IS NULL) SET @UpdateBy = CONCAT('{', @SPName, '}');

	-- Create or update Inventory record for passed in Supply.
	DECLARE @InventoryId int = (SELECT TOP 1 [Id] FROM [dbo].[Inventory] WHERE [SupplyId] = @SupplyId)
	PRINT CONCAT('@InventoryId: ''', @InventoryId, '''');
	IF (@InventoryId IS NULL) BEGIN
		INSERT INTO [dbo].[Inventory] ([SupplyId], [Quantity], [LastCost], [OverrideCost], [LastUpdateBy])
			VALUES	(@SupplyId, @Quantity, @CostPer, @CostPer, @UpdateBy);
	END ELSE BEGIN
		UPDATE	[dbo].[Inventory]
		SET		[Quantity] = [Quantity] + @Quantity,
				[LastCost] = @CostPer,
				--[OverrideCost] = (CASE WHEN [OverrideCost] IS NULL THEN @CostPer ELSE [OverrideCost] END)
				[LastUpdateBy] = @UpdateBy
		WHERE	[Id] = @InventoryId;
	END

	-- Create or update InventoryLocation record for passed in Supply.
	DECLARE @InventoryLocationId int = (	SELECT TOP 1 [Id] 
											FROM	[dbo].[InventoryLocation] 
											WHERE	[SupplyId] = @SupplyId AND [LocationId] = @LocationId);
	PRINT CONCAT('@InventoryLocationId: ''', @InventoryLocationId, '''');
	IF (@InventoryLocationId IS NULL) BEGIN
		INSERT INTO [dbo].[InventoryLocation] ([SupplyId], [Quantity], [LocationId], [LastUpdateBy])
			VALUES	(@SupplyId, @Quantity, @LocationId, @UpdateBy);
	END ELSE BEGIN
		UPDATE	[dbo].[InventoryLocation]
		SET		[Quantity] = [Quantity] + @Quantity,
				[LastUpdateBy] = @UpdateBy
		WHERE	[Id] = @InventoryLocationId;
	END

	-- Add an InventoryTransaction to record this transaction.
	DECLARE @InvTranTypeId smallint = (SELECT [Id] FROM [dbo].[lkInventoryTransactionType] WHERE [Code] = 'PRCHS');
	INSERT INTO [dbo].[InventoryTransaction] ([InventoryTransactionTypeId], [SupplyId], [SupplyCompanyId], [Quantity], [Cost], [LastUpdateBy])
		VALUES	(@InvTranTypeId, @SupplyId, @SupplyCompanyId, @Quantity, @CostPer, @UpdateBy);


EXIT_HANDLER:
END TRY
BEGIN CATCH
	IF (XACT_STATE()=-1 OR XACT_STATE()=1) ROLLBACK TRANSACTION;
	DECLARE @ErrStr nvarchar(4000);
	EXEC [adm].[usp_HandleError] @@PROCID, @ErrStr OUTPUT;
	THROW;
END CATCH
IF ((XACT_STATE())=1) COMMIT TRANSACTION;
END

