		-- No need to validate unique version as this field has a unique constraint.
		SET @ErrMsg = 'Passed in @Parameter1 can not be null.';
		RAISERROR (@ErrMsg, 16 , -1);
	END

	--DECLARE @n int=1, @d int=0, @x int; SET @x=@n/@d;

EXIT_HANDLER:
END TRY
BEGIN CATCH
